style.css:
@-moz-document domain("chickensmoothie.com") {
#page-header
{
    background:url(http://orig14.deviantart.net/0156/f/2015/193/7/6/jokerdog_by_silverhorseshoos-d912w7s.jpg) !important;
    margin: auto;
    background-repeat: no-repeat;
    max-width: 1024px;
    border-radius: 6px;
}

.liner
{
    background-color: #87879f;
}

#body_inner
{
    background: url(http://orig14.deviantart.net/7c2e/f/2015/193/3/b/fgtgztshrtsj_by_silverhorseshoos-d912yan.jpg) ;
}

.panel, .navbar, #page-header, #whoisonline, .forumbg, .rules, .liner, .unreadpost, .forabg
{
    box-shadow: 1px 1px 3px;
}

h2 a, #page-body .linklist a, h2 , .pagination , .left, p a , .pagination a, h3, p, label, .right, .post
{
    color:black !important;
}
}

@-moz-document url("http://www.chickensmoothie.com/trades/"), url("http://www.chickensmoothie.com/trades/tradingcenter.php"), url("http://www.chickensmoothie.com/trades/tradingsettings.php"), url("http://www.chickensmoothie.com/trades/findpartner.php"), url-prefix("http://www.chickensmoothie.com/adoptables/"), url-prefix("http://www.chickensmoothie.com/accounts/"), url-prefix("http://www.chickensmoothie.com/pound"), url-prefix("http://www.chickensmoothie.com/archive"), url-prefix("http://www.chickensmoothie.com/help/"), url-prefix("http://www.chickensmoothie.com/payments/balance"), url-prefix("http://www.chickensmoothie.com/store/"), url-prefix("http://www.chickensmoothie.com/trades/edittrade.php?partner="), url-prefix("http://www.chickensmoothie.com/trades/sharetrade.php?id=") {
#csbody
{
    box-shadow: 1px 1px 3px;
}
}

@-moz-document url-prefix("http://www.chickensmoothie.com/candy/") {
#csbody
{
    box-shadow: 1px 1px 3px;
}
}
