style.css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  body {
    background: url("http://img08.deviantart.net/6139/i/2014/218/9/0/mega_tyranitar___stone_edge_by_ishmam-d7o4mse.png") !important;
    background-size: cover;
  }

  .ps-overlay {
    background: rgba(0, 0, 0, 0.35);
  }

  .ps-room.ps-room-light {
    background: transparent;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    color: #000;
  }

  .ps-room.ps-room-light.scrollable {
    background: rgba(207, 184, 149, 0.4);
    border-left: 1px solid #000;
    color: #000;
  }
  
  .mainmenuwrapper {
    background: transparent;
  }

  .pad {
    padding: 0px;
  }

  .roomlist a.ilink {
    border-color: #000;
    box-shadow: none;
    background: rgba(133, 54, 62, 0.6);
    color: #000;
    text-shadow: none;
    margin-left: 25px;
  }

  .roomlist a.ilink:hover {
    box-shadow: 0px 0px 3px #BA616A;
    background: rgba(186, 97, 106, 0.8);
    color: #000;
    text-shadow: none;
    border-color: #000;
  }

  .roomlist a.ilink small {
    color: #050505;
    text-shadow: none;
  }

  .roomlist h2 {
    border: 1px solid #000;
    border-left: none;
    padding-left: 15px;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    background: rgba(88, 88, 87, 0.5);
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
    color: #FFF;
  }

