    background: transparent;
  }

  .header .logo {
    float: left;
    margin: 8px 0px 0px 2px;
  }

  .maintabbarbottom {
    background: #585857;
    border: none;
    border-bottom: 1px solid #000;
    bottom: 43px;
  }

  .tabbar a.button.notifying {
    background: #BE6B72;
    border-color: #611E25;
    color: #611E25;
    text-shadow: none;
  }

  .tabbar a.button.notifying:hover {
