style.css:
@-moz-document domain(diamondhunt.co){

a.a-tag-more-stats:hover
{
	color:gray;
}
  
  table.top-bar-stats-external td:hover
{
	background-color:gray;
}
  
  span.dialogue-option:hover
{
	color:gray;
}
  
  .container-box-blue
{
	border:1px solid gray;
	background-color: #F0FFFF;
	padding:5px 20px;
	margin:5px 10px;
}
 
  
  .big-button:hover
{
	background: -webkit-linear-gradient(black, gray); /* For Safari 5.1 to 6.0 */
	background: -o-linear-gradient(black, gray); /* For Opera 11.1 to 12.0 */
	background: -moz-linear-gradient(black,gray); /* For Firefox 3.6 to 15 */
	background: linear-gradient(black, gray); /* Standard syntax */
	cursor: pointer;
}
  
  
  .container-box-blue
{
	border:1px solid black;
	background-color: #F0FFFF;
	padding:5px 20px;
	margin:5px 10px;
}
  
  
  span.top-links
{
	color:#333;
	cursor:pointer;
}
  
  
a.top-links-a
{
	text-decoration:none;
	color:#333;
}
  
  
  span.notif-box
{
	display:inline-block;
	margin:10px 10px 5px 0px;
	color:#777;
	border:1px solid black;
	padding:5px 10px;
	font-size:14pt;
	background: -webkit-linear-gradient(#222, #555); /* For Safari 5.1 to 6.0 */
	background: -o-linear-gradient(#222, #555); /* For Opera 11.1 to 12.0 */
	background: -moz-linear-gradient(#222, #555); /* For Firefox 3.6 to 15 */
	background: linear-gradient(#222, #555); /* Standard syntax */
}
  
  
table.table-quest-list tr th
{
	border: 1px solid black;
	background-color:#333;
}
  
  
  table.table-quest-list tr:hover
{
	border: 1px solid black;
	background-color:#333;
}
  
  
  table.table-quest-list
{
	width:80%;
	background-color:#444;
	text-align:center;
	table-layout: fixed;
	border-collapse: collapse;
	cursor:pointer;
}
  
  
  .quest-label
{
	display:inline-block;
	margin:10px 20px;
	padding:5px 20px;
	border:1px solid black;
	color:white;
	background: -webkit-linear-gradient(#333, #555); /* For Safari 5.1 to 6.0 */
	background: -o-linear-gradient(#333, #555); /* For Opera 11.1 to 12.0 */
	background: -moz-linear-gradient(#333, #555); /* For Firefox 3.6 to 15 */
	background: linear-gradient(#333, #555); /* Standard syntax */
}
  
  
  .item-box-color:hover
{
	background: none;
	background-color: #333
}
  
  
  
 .item-box-color
{
	background: -webkit-linear-gradient(#333, #555); /* For Safari 5.1 to 6.0 */
	background: -o-linear-gradient(#333, #555); /* For Opera 11.1 to 12.0 */
	background: -moz-linear-gradient(#333, #555); /* For Firefox 3.6 to 15 */
	background: linear-gradient(#333, #555); /* Standard syntax */
}
  
  div.hero {
    width: 250px;
    display: inline-block;
    margin-right: 50px;
    text-align: center;
    float: left;
    color: black;
    font-size: 20pt;
}
  
  
  table.table-hero-stats tr {
    background: -webkit-linear-gradient(#444, #666);
    background: -o-linear-gradient(#444, #666);
    background: -moz-linear-gradient(#444, #666);
    background: linear-gradient(#444, #666);
}
  
  
span.item-box {
    float: left;
    text-align: center;
    border: 1px solid silver;
    width: 130px;
    color: #111;
    cursor: pointer;
    margin: 10px 10px;
    display: inline-block;
}
  
  
  
 h1.container-title {
    text-align: center;
    color: #444 !important;
}
  
  .input-button-img-hiscores {
    background-color: #444 !important;
    border: 1px solid black;
    padding: 5px;
    width: 30px;
    height: 30px;
}
  
  table.hiscores-table td {
    border: 1px solid #555;
}
  
  table.hiscores-table {
    border-collapse: collapse;
    background-color: silver;
    width: 100%;
    border: 1px solid #555 !important;
    background: -webkit-linear-gradient(#222, #444);
    background: -o-linear-gradient(#222, #444);
    background: -moz-linear-gradient(#222, #444);
    background: linear-gradient(#222, #444);
    text-align: center;
    width: 100%;
    padding: 5px 20px;
}
  
  div.skill-xp-bar {
    border: 1px solid black;
    height: 30px;
    color: #555;
    display: block;
    text-align: center;
    min-width: 100%;
    position: relative;
    background-color: #555;
}
  
  table.outer-skill-box {
    border: 1px solid #555 !important;
    background: -webkit-linear-gradient(#333, #555);
    background: -o-linear-gradient(#333, #555);
    background: -moz-linear-gradient(#333, #555);
    background: linear-gradient(#3332, #555);
    text-align: center;
    width: 100%;
    padding: 5px 20px;
}
  
  div.inner-skill-xp-bar {
    height: 30px;
    background: -webkit-linear-gradient(#222, #444);
    background: -o-linear-gradient(#222, #444);
    background: -moz-linear-gradient(#222, #444);
    background: linear-gradient(#222, #444);
    float: left;
    min-width: 0%;
    max-width: 0%;
    z-index: 0;
    position: relative;
}
  
  table.top-bar {
    margin-top: 10px;
    background-color: #121212;
    border: 1px solid gray;
    color: #555;
    table-layout: fixed;
    width: 100%;
    color: #555;
}
  
  
  div.div-chat {
    margin-top: 10px;
    font-weight: bold;
    border: 1px solid #333;
    background: -webkit-linear-gradient(#222, #444);
    background: -o-linear-gradient(#222, #444);
    background: -moz-linea	r-gradient(#222, #444);
    background: linear-gradient(#222, #444);
    padding: 5px 5px;
}
  
  div.div-chat-area {
    border: 1px solid grey;
    background-color: #444;
    height: 200px;
    padding-left: 5px;
    overflow-y: auto;
}
  
  table.tab-bar td:hover
{
	background:none;
	background: -webkit-linear-gradient(#111, #555) !important; /* For Safari 5.1 to 6.0 */
	background: -o-linear-gradient(#111, #555) !important; /* For Opera 11.1 to 12.0 */
	background: -moz-linear-gradient(#111, #555) !important; /* For Firefox 3.6 to 15 */
	background: linear-gradient(#111, #555) !important; /* Standard syntax */
}

table.tab-bar td {
    border: 1px solid grey;
    cursor: pointer;
    height: 60px;
    padding: 1px 5px;
}

}
