style.css:
@-moz-document url-prefix("http://coccoc.com/") {
#top-block {
    display: none !important;
}

div[class="right"] {
    display: none !important;
}

div[class="right-wrapper"] {
    display: none !important;
}

div[class="ntrb-wrapper"] {
    display: none !important;
}

#popular {
    display: none !important;
}

#search-example {
    display: none !important;
}

#middle-block {
	position: relative !important;
	top: 0px !important;
	align-items: center;
	justify-content: space-between;
}

div[class="left"] {
    width: 100%;
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    align-items: center;
    justify-content: space-between;
}

#soccer-score {
display: none !important;
}

#wc-toggle-design {
display: none !important;
}

#hint-popup {
display: none !important;
}

#hint-pointer {
display: none !important;
}

#top-notification {
display: none !important;
}
}
