style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slaent.com") {

.spoiler{
    padding-left: 5px !important;
    padding-right: 5px !important;
}
.navigation{
    left: 0px !important;    
}
.layout--wrapper::after{
    background-color: rgba(0,0,0,0) !important;
}
.navigation__background::before{
    background-image: linear-gradient(to left, rgba(0, 0, 0, 0.39), rgba(14, 14, 14, 0.52)) !important;
}   
.promoted-thread__item::after{
    background-image: linear-gradient(rgba(30, 30, 30, 0), rgba(30,30,30,50)) !important;
}    
.thread__cover::after {
    background-color: rgba(0,0,0,0) !important;
}
.thread__background::before{
    background-image: linear-gradient(rgba(255, 255, 255, 0), rgba(30, 30, 30, 255)) !important;
}    
.thread__cover{
    background-color: rgba(30,30,30,255) !important;
}    
.navigation__icon--user::before{
    margin-top: -5px !important;
}    
}
