style.css:
@-moz-document domain("tweetdeck.twitter.com") {
/* App */

.app-header {
    box-shadow: 0px 0px 5px #111;
}

.js-app-columns-container {
    margin-left: 5px;
    margin-right: 5px;
}

.app-title {
    background: none;
    padding-bottom: 5px;
}
    
.tweet-footer .txt-mute {
    display: none;
}
    
.stream-item .tweet-footer {
    opacity: 0.2;
    transition: opacity 0.05s ease-in-out
}
    
.stream-item:hover .tweet-footer {
    opacity: 1;
}

.app-title .sprite-logo-badge {
    background-image: url(https://file.quad.moe/file/F16D05.png);
    background-position: 0 0;
    background-size: contain;
}

.app-header, .column-nav-item, .app-navigator {
    background-color: #151822;
}

.js-modal-panel, .mdl-header {
    color: #fff;
}

/* Columns */
.column {
    border: none;
    margin-right: 5px;
}

.column-header {
    border: none;
}

.column-header-temp .icon, .js-title {
    color: #111;
}

.column-message {
    border: none;
}

/* Standard BG color */

.app-columns-container, .js-column-content, .column-header, .js-tweet-detail, .js-tweet-replies, .js-detail-content, .scroll-conversation, .column-message, .column-content, .app-content, .js-modal-panel {
    background-color: #263238;
}

/* Icons */

.icon {
    color: #FFF;
}

.icon-user-dd, .icon-play-video {
    color: #555;
}

/* Buttons */

.btn, .js-action-button {
    box-shadow: 0.5px 0.5px 1.5px #111;
    background-color: #2196F3;
    border: none;
    transition: background 0.1s ease-out;
}

.btn:hover, .js-action-button:hover {
    background-color: #64B5F6;
}

.btn-square {
    background: none;
    box-shadow: none;
}

.is-disabled {
    background-color: #455A64 !important;
    color: #90A4AE !important;
}

.btn-neutral, .box-neutral:hover {
    background: none !important;
    box-shadow: none !important;
}

.btn-back, .btn-back:hover {
    color: #fff;
}

/* Compose */

.compose {
    background-color: #37474F;
}

.btn-compose, .js-hide-drawer {
    background-color: #37474F !important;
}

.js-show-drawer {
    background-color: #2196F3 !important;
}

.js-show-drawer:hover {
    background-color: #64B5F6 !important;
}

.compose-text-container {
    box-shadow: 1px 1px 3px #111;;
}

.bg-r-white {
    background-color: #AAAAAA;
}

.js-quote-tweet-holder div .js-quoted-tweet-text {
    color: #555;
}

.js-quote-tweet-holder div .username {
    color: #333;
}

.js-quote-tweet-holder div .link-complex-target {
    color: #111;
}

.js-header-action {
    background: none !important;
}

/* Actually, fuck the scrollbar. This was just made to look pretty anyways */

.scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track {
    border: none;
}

.scroll-styled-v::-webkit-scrollbar {
    display: none;
}

/* Tweets */

/*[[stream-item]]*/

.link-complex, .url-ext {
    color: #00BCD4;
    transition: color 0.1s ease-out;
}

.fullname {
    color: #e2e2e2;
}

.link-complex:hover, .url-ext:hover {
    color: #4DD0E1;
}

.account-link {
    color: #e2e2e2;
    transition: color 0.1s ease-out;
}

.account-link:hover {
    color: #fff;
}

.stream-item:hover {
    background-color: #455A64;
}

/* Avatars */

.avatar, .compose-account-img, .size48, .js-account-item {
    border-radius: 100px;
    transition: transform 0.1s ease-out;
}

.avatar:hover, .compose-account-img:hover, .size48:hover, .js-account-item:hover {
    transform: scale(1.1,1.1);
}

.avatar:active, .compose-account-img:active, .size48:active, .js-account-item:active {
    transform: scale(0.9,0.9);
}
  
.s-profile, .prf {
    border-radius: 0px;
}

/* Detail view */

.detail-view-inline {
    background-color: #222226;
    border: none;
}

.js-tweet-detail {
    border: none;
}
}

