style.css:
@-moz-document domain('store.steampowered.com'), domain('steamcommunity.com'), domain('help.steampowered.com') {
/* --- BEGIN --- */

/*
 * Flat Steam UI
 * Created by Denis G. <https://github.com/denis-g/>
 * Last update: 2018-22-11
 *
 * Use GitHub or Steam Group for submit your request or issues.
 * https://github.com/denis-g/userstyle-flat-steam-ui/issues
 * https://steamcommunity.com/groups/flat-steam-ui/discussions/0/133259227524504224/
 */

/*
 *  Header
 *  ---------------------------------------------
 */

html body {
    padding-top: 48px; /* menu height */
    }
html body div#global_header,
html body.v6 div#global_header {
    background: rgba(23, 26, 33, 0.95);
    }
html body div#global_header {
    border: none;
    position: fixed !important;
    left: 0;
    top: 0;
    width: 100%;
    z-index: 900;
    padding-left: 0;
    padding-right: 0;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.8), 0 0 20px rgba(0, 0, 0, 0.5);
    -webkit-backdrop-filter: saturate(180%) blur(20px);
    box-sizing: border-box;
    }
    /* redesign main menu */
    html body div#global_header .content,
    html body.v6 div#global_header .content {
        background: none;
        }
    html body div#global_header .content {
        height: 48px; /* menu height */
        right: auto;
        }
    @media (min-width: 1208px) and (max-width: 1440px) {
        /* home page with sidebar */
        html body.v6.infinite_scrolling div#global_header .content {
            /* header offset */
            right: -108px !important;
            }
        }
        html body div#global_header div.logo {
            padding: 0;
            margin: 0;
            width: auto;
            height: auto;
            }
            html body div#global_header div.logo a {
                display: block;
                width: 80px;
                height: 48px; /* menu height */
                background: url('data:image/svg+xml;utf8,<svg%20xmlns="http://www.w3.org/2000/svg"%20viewBox="0%200%20233%20233"><path%20fill="%23cccccc"%20d="m4.8911%20150.01c14.393%2048.01%2058.916%2082.99%20111.61%2082.99%2064.34%200%20116.5-52.16%20116.5-116.5%200-64.341-52.16-116.5-116.5-116.5-61.741%200-112.26%2048.029-116.25%20108.76%207.5391%2012.66%2010.481%2020.49%204.6411%2041.25z"%20/><path%20d="m110.5%2087.322c0%200.196%200%200.392%200.01%200.576l-28.508%2041.412c-4.618-0.21-9.252%200.6-13.646%202.41-1.937%200.79-3.752%201.76-5.455%202.88l-62.599-25.77c0.00049%200-1.4485%2023.83%204.588%2041.59l44.254%2018.26c2.222%209.93%209.034%2018.64%2019.084%2022.83%2016.443%206.87%2035.402-0.96%2042.242-17.41%201.78-4.3%202.61-8.81%202.49-13.31l40.79-29.15c0.33%200.01%200.67%200.02%201%200.02%2024.41%200%2044.25-19.9%2044.25-44.338%200-24.44-19.84-44.322-44.25-44.322-24.4%200-44.25%2019.882-44.25%2044.322zm-6.84%2083.918c-5.294%2012.71-19.9%2018.74-32.596%2013.45-5.857-2.44-10.279-6.91-12.83-12.24l14.405%205.97c9.363%203.9%2020.105-0.54%2023.997-9.9%203.904-9.37-0.525-20.13-9.883-24.03l-14.891-6.17c5.746-2.18%2012.278-2.26%2018.381%200.28%206.153%202.56%2010.927%207.38%2013.457%2013.54s2.52%2012.96-0.04%2019.1m51.09-54.38c-16.25%200-29.48-13.25-29.48-29.538%200-16.275%2013.23-29.529%2029.48-29.529%2016.26%200%2029.49%2013.254%2029.49%2029.529%200%2016.288-13.23%2029.538-29.49%2029.538m-22.09-29.583c0-12.253%209.92-22.191%2022.14-22.191%2012.23%200%2022.15%209.938%2022.15%2022.191%200%2012.254-9.92%2022.183-22.15%2022.183-12.22%200-22.14-9.929-22.14-22.183z"%20/></svg>') no-repeat 0 50%;
                background-size: auto 32px;
                opacity: 0.7;
                }
            html body div#global_header div.logo a:hover {
                opacity: 1;
                }
            html body div#global_header div.logo img {
                display: none;
                }
        html body div#global_header .supernav_container {
            left: 60px;
            }
            html body div#global_header .menuitem {
                max-width: 170px;
                text-align: center;
                padding: 0 20px;
                font-size: 13px;
                line-height: 48px; /* menu height */
                border-right: 1px solid rgba(255, 255, 255, 0.1);
                overflow: hidden;
                text-overflow: ellipsis;
                white-space: nowrap;
                }
            html body div#global_header .menuitem:first-child {
                border-left: 1px solid rgba(255, 255, 255, 0.1);
                }
            html body div#global_header .menuitem:hover {
                color: #000;
                background: #ccc;
                }
                html body div#global_header .menuitem[href="https://steamcommunity.com/chat/"] {
                    font: 0/0 a;
                    width: auto;
                    }
                html body div#global_header .menuitem[href="https://steamcommunity.com/chat/"]:hover {
                    background: none;
                    }
                    html body div#global_header .menuitem[href="https://steamcommunity.com/chat/"]:before {
                        content: '';
                        display: inline-block;
                        vertical-align: middle;
                        width: 20px;
                        height: 48px; /* menu height */
                        background: url('data:image/svg+xml;utf8,<svg%20xmlns="http://www.w3.org/2000/svg"%20viewBox="0%200%20297%20297"><path%20fill="%23cccccc"%20d="M206.093,181.516c17.379,0,31.515-14.138,31.515-31.515V54.593c0-17.378-14.136-31.518-31.515-31.518H31.51C14.135,23.075,0,37.215,0,54.593v95.408c0,17.377,14.135,31.515,31.51,31.515h22.933v32.988c0,4.012,2.416,7.627,6.121,9.162c1.228,0.507,2.516,0.753,3.792,0.753c2.58,0,5.117-1.007,7.014-2.904l39.991-39.999H206.093z"/><path%20fill="%23cccccc"%20d="M269.415,105.583h-15.944v44.418c0,26.125-21.253,47.378-47.378,47.378h-88.161l-18.537,18.542c2.234,12.987,13.567,22.902,27.181,22.902h76.762l32.191,32.196c1.896,1.897,4.434,2.905,7.014,2.905c1.276,0,2.564-0.249,3.792-0.756c3.705-1.535,6.12-5.149,6.12-9.16v-25.186h16.961c15.211,0,27.585-12.378,27.585-27.591v-78.061C297,117.959,284.626,105.583,269.415,105.583z"/></svg>') no-repeat 50%;
                        background-size: auto 20px;
                        opacity: 0.7;
                        }
                    html body div#global_header .menuitem[href="https://steamcommunity.com/chat/"]:hover:before {
                        opacity: 1;
                        }
                html body div#global_header .submenuitem {
                    color: #8F98A0;
                    font-size: 12px;
                    font-weight: normal;;
                    line-height: 1.1;
                    padding: 10px 20px;
                    }
                html body div#global_header .submenuitem:not(:last-child) {
                    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
                    }
                html body div#global_header .submenuitem:hover {
                    color: #000;
                    background: #ccc;
                    }
            html body div#global_header .supernav_content {
                padding: 0;
                width: 191px;
                max-width: none;
                margin: 3px 0 0 -1px;
                background: rgba(23, 26, 33, 0.95);
                -webkit-backdrop-filter: saturate(180%) blur(20px);
                }
            html body div#global_header div[data-submenuid] {
                width: 100%;
                max-width: none;
                }
        /* fix price in userbar */
        html body div#global_actions a.global_action_link {
            white-space: nowrap;
            }
        /* user menu */
        html body #account_pulldown {
            max-width: 130px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
            }
        html body #language_pulldown.focus,
        html body #account_pulldown.focus {
            border: none;
            }
        html body div#global_action_menu .popup_block_new {
            }
            html body div#global_action_menu .popup_menu .popup_menu_item[href="javascript:Logout();"] {
                color: #b03500;
                font-weight: bold;
                }
            html body div#global_action_menu .popup_menu .popup_menu_item[href="javascript:Logout();"]:hover {
                color: #fff;
                background: #b03500;
                }
            /* specially for notifications */
            html body div#global_action_menu .popup_block_new#header_notification_dropdown {
                }
                html body div#global_action_menu .popup_block_new#header_notification_dropdown .notification_count {
                    font-weight: bold;
                    }
                html body div#global_action_menu .popup_block_new#header_notification_dropdown .popup_menu_item {
                    color: #8f98a0;
                    text-align: left;
                    }
                html body div#global_action_menu .popup_block_new#header_notification_dropdown .popup_menu_item:hover {
                    color: #000;
                    }
            /* fixed height */
            html body div#global_action_menu .popup_block_new#language_dropdown {
                }
                html body div#global_action_menu .popup_block_new#language_dropdown .popup_body {
                    max-height: 260px;
                    overflow-y: auto;
                    -webkit-overflow-scrolling: touch;
                    position: relative;
                    }
                html body div#global_action_menu .popup_block_new#language_dropdown .popup_body::-webkit-scrollbar {
                    background: transparent;
                    width: 6px;
                    position: absolute;
                    }
                html body div#global_action_menu .popup_block_new#language_dropdown .popup_body::-webkit-scrollbar-track {
                    background: transparent;
                    position: absolute;
                    z-index: -2;
                    }
                html body div#global_action_menu .popup_block_new#language_dropdown .popup_body::-webkit-scrollbar-thumb {
                    border-radius: 4px;
                    background: #888;
                    }


/*
 *  Homepage
 *  ---------------------------------------------
 */

html body a.home_headercap,
html body a.home_smallcap,
html body .curated_app_item,
html body .discovery_queue_overlay,
html body .game_description_column .block .block_content a {
    border-color: rgba(255, 255, 255, 0.1);
    }
html body .carousel_container.maincap .carousel_items,
html body .home_area_spotlight,
html body .carousel_container.spotlight .carousel_items > * .store_capsule,
html body .store_capsule,
html body .big_button {
    box-shadow: none;
    }
html body .home_recommended_spotlight {
    border: 1px solid rgba(255, 255, 255, 0.1);
    margin-bottom: 0;
    }
html body #home_recommended_more {
    background: none;
    padding: 6px 0 0;
    }
html body .apps_recommended_by_curators_ctn {
    margin-bottom: 20px;
    }
html body .apps_recommended_by_curators_bottom_bar {
    background: none;
    padding: 6px 0 0;
    margin: 0;
    }
html body #apps_recommended_by_curators::-webkit-scrollbar {
    display: none;
    }
html body .steam_curator {
    vertical-align: top;
    }
html body .home_specials_grid .special {
    border-color: rgba( 255, 255, 255, 0.1);
    }
html body .home_block_footer {
    background: none;
    padding: 2px 0 0;
    }
html body .home_content.single {
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: none;
    }
html body .home_content.single a.screenshot {
    width: 212px;
    }
html body .home_content.single_buttonbar {
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-top: none;
    margin-top: 0;
    }


/*
 *  Games
 *  ---------------------------------------------
 */

 html body #agegate_box select {
     padding-right: 30px;
     }

html body .rightcol.game_meta_data .block,
html body .explore_rightcol_box {
    border: 1px solid rgba(255, 255, 255, 0.1);
    }
html body .rightcol.game_meta_data .block {
    padding: 14px;
    }
html body .rightcol.game_meta_data .block_content_inner {
    border: none;
    }

html body .review_box {
    position: relative;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.05);
    }
    html body .review_box_content {
        position: relative;
        }
    html body .review_box .leftcol {
        float: none;
        width: 300px;
        padding: 20px;
        margin: 0;
        opacity: 1;
        }
    html body .friend_reviews .review_box .leftcol {
        margin-top: 0;
        }
    html body .review_box .rightcol {
        float: none;
        width: auto;
        padding: 0 20px;
        margin: 0;
        overflow: inherit;
        position: static;
        }
    html body .review_box .vote_header {
        float: right;
        width: 230px;
        margin: 0;
        position: absolute;
        top: 20px;
        right: 20px;
        }
    html body .review_box .content {
        margin: 0;
        }
    html body .review_box blockquote.bb_blockquote,
    html body .review_box .bb_wysiwyg blockquote {
        margin: 0 0 10px;
        }
    html body .review_box .posted {
        margin-left: 0;
        margin-right: 0;
        }
    html body .review_box .postedDate {
        clear: both;
        display: block;
        }
        html body .review_box .postedDate br {
            display: none;
            }
    html body .review_box .control_block {
        padding-left: 0;
        padding-right: 0;
        }

html body .review_page_content {
    }
    html body .review_page_content #leftContents {
        background: rgba(0, 0, 0, 0.2);
        border: 1px solid rgba(255, 255, 255, 0.05);
        }
        html body .review_page_content .ratingSummaryBlock {
            padding: 20px 20px 0;
            }
        html body .review_page_content .review_area {
            padding: 20px;
            }
        html body .review_page_content .commentthread_area {
            padding: 0 20px 20px;
            }

html body .game_description_column .block_header {
    border: none;
    }

html body .game_area_play_stats {
    padding-top: 20px;
    }
html body .already_owned_actions {
    display: inline-block;
    vertical-align: top;
    padding-top: 0;
    }
html body .game_area_already_owned_btn {
    float: none;
    margin-right: 0;
    display: inline-block;
    vertical-align: top;
    }
    html body .game_area_already_owned_btn a[href="http://store.steampowered.com/about/"] {
        /* remove 'Install Steam' link */
        display: none;
        }
html body .already_owned_actions + .game_area_already_owned_btn {
    margin-left: 12px;
    }
html body .dynamiclink_box {
    margin-right: 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    }

html body #review_recent_events_container {
    box-shadow: none;
    }
html body .user_reviews_header {
    background: none;
    }
    html body #review_histograms_container {
        box-shadow: none;
        }
        html body .review_histogram_section .user_reviews_summary_bar {
            border-bottom: none;
            }
        html body .review_histogram_section.recent {
            box-shadow: none;
            }
    html body .user_reviews_summary_bar .game_review_summary {
        text-shadow: none;
        }
    html body .user_reviews_filter_options {
        border: 1px solid rgba(255, 255, 255, 0.1);
        }
        html body .user_reviews_filter_score {
            border-top-color: rgba(255, 255, 255, 0.1);
            }
        html body .user_reviews_filter_score .game_review_summary:after {
            content: '- ' attr(data-tooltip-text);
            display: inline-block;
            vertical-align: middle;
            font-size: 11px;
            font-weight: lighter;
            margin-left: 0.5em;
            opacity: 0.6;
            }
    html body .user_reviews_filter_menu {
        }
        html body .user_reviews_filter_menu .title {
            padding-top: 12px;
            }

html body .app_tag:hover,
html body .app_tag.add_your_own:hover,
html body .app_tag.previous_tag:hover {
    background: #67c1f5 !important;
    }

html body .block.responsive_apppage_details_left .btn_medium > span {
    font-size: 12px;
    }

html body .game_hover_box {
    background: rgba(227, 234, 239, 0.95);
    -webkit-backdrop-filter: saturate(150%) blur(5px);
    }
html body .game_hover .hover_arrow_left,
html body .game_hover .hover_arrow_right {
    display: none !important;
    }

html body .achieveRow:not(.unlocked) .compareImg {
    opacity: 0.5;
    filter: blur(1px);
    }


/*
 *  Wishlist
 *  ---------------------------------------------
 */

html body #wishlist_search {
    vertical-align: top;
    padding: 11px 10px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: none;
    }
    html body #wishlist_search:hover,
    html body #wishlist_search:active {
        border: 1px solid rgba(255, 255, 255, 0.4);
        box-shadow: none;
        }


/*
 *  Badges
 *  ---------------------------------------------
 */

html body .profile_xp_block {
    border-color: #1a1819;
    }
html body .profile_xp_block_remaining_bar {
    height: 10px;
    padding: 1px;
    border: 1px solid #76b7e4;
    background: #232323;
    }
html body .profile_xp_block_remaining_bar_progress {
    height: 10px;
    background: #76b7e4;
    }
html body .badge_row.is_link:hover,
html body .badge_row.depressed {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    }
html body .badge_row {
    border: 1px solid #000;
    padding: 0;
    background: none;
    }
    html body .badge_row_inner {
        background: #141414;
        }

html body #market_buy_commodity_input_price,
html body #market_buy_commodity_input_quantity {
    font-size: 14px;
    color: rgba(255, 255, 255, 0.8);
    padding: 5px 6px;
    text-align: center;
    }


/*
 *  Login
 *  ---------------------------------------------
 */

html body .mainLoginPanel {
    box-shadow: none;
    border-radius: 0;
    background: rgba( 64, 128, 183, 0.4);
    }
html body .authcode_entry_box,
html body .twofactorauthcode_entry_box {
    width: auto;
    height: auto;
    background-image: none;
    }
html body input.authcode_entry_input,
html body input.twofactorauthcode_entry_input {
    width: 100%;
    padding: 14px 10px;
    margin: 0;
    font-size: 40px;
    text-align: center;
    letter-spacing: 0.25em;
    box-sizing: border-box;
    }
    html body input.authcode_entry_input::-webkit-input-placeholder,
    html body input.twofactorauthcode_entry_input::-webkit-input-placeholder {
        letter-spacing: normal;
        font-size: 20px;
        text-align: center;
        }
    html body input.authcode_entry_input::-moz-placeholder,
    html body input.twofactorauthcode_entry_input::-moz-placeholder {
        letter-spacing: normal;
        font-size: 20px;
        text-align: center;
        }
html body .help_section_content,
html body .account_recovery_box {
    border: 1px solid rgba(255, 255, 255, 0.1);
    }
html body .help_wizard_button,
html body .help_wizard_button:not(.disabled):not(.nohover):hover,
html body .help_wizard_button.opened {
    border: 1px solid rgba(255, 255, 255, 0.2);
    }


/*
 *  Profile
 *  ---------------------------------------------
 */

html body .profile_small_header_bg,
html body .profile_header_bg,
html body .profile_subpage_column .profile_small_header_bg {
    background: none;
    }
html body .profile_header_bg {
    padding-bottom: 14px;
    z-index: 2;
    }
html body .profile_subpage_column .profile_small_header_bg {
    height: 104px;
    }
html body .profile_small_header_texture,
html body .profile_header_bg_texture {
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-top: none;
    box-shadow: 0 20px 20px rgba(0, 0, 0, 0.1);
    }
html body .profile_small_header_texture {
    background: rgba(41, 53, 72, 0.85);
    }
html body .profile_header_bg_texture {
    background: rgba(41, 48, 62, 0.85);
    -webkit-backdrop-filter: saturate(150%) blur(5px);
    }
html body .profile_background_overlay_content {
    opacity: 1;
    background: rgba(23, 23, 23, 0.85);
    -webkit-backdrop-filter: saturate(150%) blur(5px);
    }
html body .profile_customization {
    background: rgba(34, 45, 62, 0.6);
    border: 1px solid rgba(255, 255, 255, 0.05);
    -webkit-backdrop-filter: saturate(150%) blur(5px);
    }
html body .profile_subpage_general .maincontent {
    border: 1px solid rgba(255, 255, 255, 0.075);
    border-top: none;
    background-color: rgba(33, 33, 33, 0.7);
    box-shadow: none;
    }

html body #NamePopup.popup_block_new .popup_menu {
    background: rgba(0, 0, 0, 0.75);
    border: 1px solid rgba(0, 0, 0, 0.4);
    }
    html body #NamePopup.popup_block_new .popup_menu > div:first-child:not(#NamePopupAliases) {
        padding: 4px 12px 0;
        font-size: 11px;
        opacity: 0.5;
        }

html body .profile_header_actions {
    text-align: right;
    }
    html body .profile_header_actions .popup_menu a.popup_menu_item {
        text-align: left;
        }

html body .btn_profile_action {
    display: inline-block;
    vertical-align: top;
    text-align: center;
    color: rgba(255, 255, 255, 0.4) !important;
    border: 1px solid rgba(0, 0, 0, 0.3);
    background: rgba(0, 0, 0, 0.2) !important;
    }
    html body .btn_profile_action:hover,
    html body .btn_profile_action.focus {
        color: rgba(255, 255, 255, 0.8) !important;
        border-color: rgba(0, 0, 0, 0.6);
        background: rgba(0, 0, 0, 0.3) !important;
        }
    html body .btn_profile_action > span {
        font-size: 8px;
        font-weight: bold;
        line-height: 1;
        text-transform: uppercase;
        letter-spacing: 0.10em;
        padding: 8px 10px 6px;
        background: none !important;
        }
    html body .btn_profile_action img {
        vertical-align: middle;
        -webkit-filter: grayscale(100%);
        filter: grayscale(100%);
        }
    html body .btn_profile_action > span,
    html body .btn_profile_action:hover > span,
    html body .btn_profile_action.focus > span {
        background: none;
        }

html body .showcase_stat.favoritegroup_inchat {
    background: rgba(34, 45, 62, 0.4);
