style.css:
/*  Name: Reddit - Dark - sidebar (m.reddit.com) */

/*  A dark css for the new mobile
    version of reddit.com */
   
   
/*
  Created by budRich 2015
*/


@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url-prefix("http://m.reddit.com/"),
  url-prefix("https://m.reddit.com/") {

html, main {
    background: #202020 !important;
}  
      
.TopNav {
    top: -44px !important;
}

.TopNav:hover {
    top: 0px !important;
}

.Listing-container {
    top: -5px !important;
}      

.TopSubnav {
    color: #AAA;
    margin-top: -44px !important;
}      

.Listing-header {
    background-color: #282828;

}      

.Listing-footer {
   background-color: #282828;
}
      
.Listing-vote {
    color: #8E8E8E;
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0) 0px, #282828 15%);
    background-repeat: repeat-x;
}      
      
a {
    color: #AAA !important;
}    
      
a:focus, a:hover {
    color: #AAA !important;
}
      
.btn-primary {
    color: #126D92;
    background-color: #282828;
    border-color: #444;
}    
      
.btn-primary:hover {
    color: #126D92;
    background-color: #333;
    border-color: #444;
}       
      
.ListingContent-text, input.zoom-fix, textarea.zoom-fix, .comment {
    background-color: #343434;
    color: #CCC;

}      
      
      
}
