style.css:
@-moz-document domain("se7ensins.com") {
/*Logo*/
#logo img {
    max-width: 370px;
    width: 0;
    height: 90px;
    padding-right: 385px;
    background: transparent url(http://i.imgur.com/Mk1xVaf.png) top left no-repeat;
}
/*Fixes*/
html .navTabs .navTab.PopupOpen .navLink {
    color: rgb(234, 234, 234);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
.mast .subHeading, .navigationSideBar .subHeading, .navigationSideBar .heading {
    background: rgb(26, 26, 26);
    border-bottom: 3px solid rgb(255, 141, 0);
    -webkit-border-radius: 3px 3px 0px 0px;
    -moz-border-radius: 3px 3px 0px 0px;
    -khtml-border-radius: 3px 3px 0px 0px;
    border-radius: 3px 3px 0px 0px;
}
.dark_postrating_bar_positive {
    height: 10px;
    background: #ff8d00;
    display: inline-block;
    opacity: 1.0;
    font-size: 0;
}
.dark_postrating_positive {
    color: #ff8d00;
}
.XenBase .profilePage .textWithCount.subHeading .count {
    background-color: rgb(255, 141, 0);
    color: #FFF;
    border-width: 0px;
    font-size: 12px;
    font-weight: bold;
    margin-top: -1px;
}
.subHeading {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
.XenBase .subForumsPopup .PopupOpen .dt {
    color: rgb(255, 141, 0);
}
.dark_postrating_positive {
    color: #ff8d00;
}
.textCtrl:focus, .textCtrl.Focus {
    color: rgb(255, 141, 0);
    border-color: rgb(46,46,46);
}
/*NavBar Selected Tab*/
html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(255, 255, 255);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
/*NavBar Hover Tab*/
.navTabs .navTab.PopupOpen .navLink {
    color: rgb(234, 234, 234);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
.navTabs .navTab.PopupClosed .navLink:hover {
    color: rgb(234, 234, 234);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
/*NavBar Menu Border*/
.Menu {
border-top: 5px solid #ff8d00;
}
/*NavBar Menu Text*/
.blockLinksList a, .blockLinksList label {
    color: rgb(255, 141, 0);
    padding: 5px 10px;
    display: block;
    outline: 0 none;
}
/*NavBar Menu Title*/
body {
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    text-size-adjust: 100%;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    word-wrap: break-word;
    line-height: 1.28;
}
/*NavBar Border*/
.navTabs {
    font-size: 13px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    background-color: rgb(26, 26, 26);
    padding: 0 25px;
    border-top: 6px solid rgb(26, 26, 26);
    border-bottom: 6px solid rgb(255, 60, 0);
    height: 40px;
}
/*Links*/
a:link, a:visited {
    color: rgb(255, 141, 0);
    text-decoration: none;
}
/*Nav Arrow*/
.breadcrumb .crust .arrow {
    border: 12px solid transparent;
    border-right: 1px none black;
    border-left-color: rgb(255, 141, 0);
    -moz-border-right-colors: rgb(82, 125, 57);
    display: block;
    position: absolute;
    right: -12px;
    top: 0px;
    z-index: 50;
    width: 0px;
    height: 0px;
}
/*CategoryStrip*/
.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    margin: 0 0 7px;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    min-height: 6px;
}
/*NodeSprite*/
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://i.imgur.com/r4a4GQj.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
}
/*Section Links Hover*/
.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    color: rgb(255, 141, 0);
    text-decoration: none;
    background: url(http://www.se7ensins.com/rgba.php?r=0&g=0&b=0&a=0);
    background: rgba(0, 0, 0, 0);
    border-width: 0px;
    outline: 0;
}
/*More Links*/
.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt {
    color: rgb(255, 141, 0);
}
/*RSS Icon*/
.node .tinyIcon {
    font-size: 17px;
    color: rgb(255, 141, 0);
    background-color: transparent;
    margin: 1px 4px;
    display: block;
    white-space: nowrap;
    opacity: 0.25;
    width: 12px;
    height: 16px;
}
/*Arrow*/
.Popup .PopupOpen .arrowWidget:before {
    content: "\f077";
    color: rgb(255, 141, 0);
}
.Popup .arrowWidget:before {
    color: rgb(255, 141, 0);
    content: "\f078";
    display: block;
    font-family: FontAwesome;
    line-height: 13px;
    margin-left: 2px;
}
/*Sub-Forums Text*/
.subForumsPopup .dt {
    color: rgb(255, 141, 0);
}
/*Time Posted*/
body .muted, body a.muted, body .muted a {
    color: rgb(255, 255, 255);
}
/*Sidebar Title/Border*/
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 141, 0);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(255, 141, 0);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
}
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: rgb(255, 141, 0);
}
mast .subHeading, .navigationSideBar .subHeading, .navigationSideBar .heading {
    background: rgb(26, 26, 26);
    border-bottom: 3px solid rgb(255, 141, 0);
    -webkit-border-radius: 3px 3px 0px 0px;
    -moz-border-radius: 3px 3px 0px 0px;
    -khtml-border-radius: 3px 3px 0px 0px;
    border-radius: 3px 3px 0px 0px;
}
/*Footer Text*/
.footer a, .footer a:visited {
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 141, 0);
    padding: 11px 5px;
    display: block;
}
.extraFooter h3 {
    font-size: 16px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 141, 0);
    padding-bottom: 5px;
    margin: 0 auto 7px;
    font-weight: 700;
}
.footerBlock ul.footerList li a:hover {
    color: rgb(255, 141, 0);
    text-decoration: none;
}
/*Footer Border*/
.extraFooter {
    font-size: 12px;
    color: rgb(126, 126, 126);
    background-color: rgb(26, 26, 26);
    padding: 15px;
    border-top: 6px solid rgb(255, 60, 0);
    -webkit-border-top-left-radius: 0px;
    -moz-border-radius-topleft: 0px;
    -khtml-border-top-left-radius: 0px;
    border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    -khtml-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 10px;
    -moz-border-radius-bottomright: 10px;
    -khtml-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
    -webkit-border-bottom-left-radius: 10px;
    -moz-border-radius-bottomleft: 10px;
    -khtml-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
    display: block;
    overflow: hidden;
}
/*Buttons*/
.button {
    font-style: normal;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 5px;
    border: 0px solid rgb(82, 125, 57);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    text-align: center;
    outline: none;
    line-height: 26px;
    display: inline-block;
    cursor: pointer;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -moz-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -khtml-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    height: 28px;
}
a.callToAction {
    padding: 0px;
    border: 0px solid rgb(82, 125, 57);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    display: inline-block;
    line-height: 28px;
    -webkit-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -moz-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -khtml-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    outline: 0 none;
    height: 28px;
}
/*Button Hover*/
a.callToAction:hover span {
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
}
/*Misc Dropdown*/
.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    color: rgb(255, 141, 0);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    -webkit-border-bottom-right-radius: 0px;
    -moz-border-radius-bottomright: 0px;
    -khtml-border-bottom-right-radius: 0px;
    border-bottom-right-radius: 0px;
    -webkit-border-bottom-left-radius: 0px;
    -moz-border-radius-bottomleft: 0px;
    -khtml-border-bottom-left-radius: 0px;
    border-bottom-left-radius: 0px;
}
.blockLinksList a:active, .blockLinksList a.selected, .blockLinksList label:active, .blockLinksList label.selected {
    color: rgb(255, 255, 255);
    background-color: rgb(255, 141, 0);
}
/*Featured*/
.XenBase .amsArticleViewItem .main .featured, .XenBase .sidebar .featuredNotice {
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    color: #FFF;
    border: 0px solid rgb(66,98,14);
    font-size: 12px;
    line-height: 1.4;
}
/*Sidebar Text*/
.amsCategoryList .count {
    color: rgb(255, 255, 255);
    float: right;
    margin-left: 5px;
}
.amsMostRecentComments .avatarList .extraData, .amsMostRecentComments .avatarList .extraData a {
    color: rgb(255, 255, 255);
}
.navigationSideBar a:hover {
    color: rgb(255, 255, 255);
    background-image: none;
} 
/*Pages*/
.PageNav a.currentPage {
    color: rgb(234,234,234);
    background-color: rgb(255, 141, 0);
    border-color: rgb(255, 141, 0);
    position: relative;
}
.PageNav a:hover, .PageNav a:focus {
color: #fff;
text-decoration: none;
background-color: #ff8d00;
border-color: #ff8d00;
}
/*Footer Stuff*/
.footer a:hover, .footer a:active {
color: #ff8d00;
text-decoration: underline;
}
/*Username Colors*/
.username .style5 {
    color: rgba(129, 129, 129, 1);
}    
.username .style2 {
    font-weight: lighter;
    color: #C8D5D2;
} 
.username .style3 {
    font-weight: bold;
}
.username .style4 {
    color: #00ffe4;
    font-weight: bold;
}
.username .style7 {
    color: #5491C4;   
}
/*Tab Hover*/
.tabs li:hover a, .tabs.noLinks li:hover {
    color: rgb(255, 141, 0);
    text-decoration: none;
    background-color: rgb(43, 43, 43);
}
/*Message Heading*/
.messageHeading {
    font-size: 13px;
    color: #FFF;
    font-weight: bold;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    padding: 9px 10px;
    overflow: hidden;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    margin-bottom: 5px;
}
/*New Indicator*/
.message .newIndicator {
    font-weight: bold;
    font-size: 12px;
    color: rgb(234, 234, 234);
    padding: 1px 5px;
    margin: -5px -5px 5px 5px;
    border: 1px solid rgb(255, 60, 0);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    -khtml-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #ff8d00 0%,#ff3c00 100%);
    display: block;
    float: right;
    position: relative;
    -webkit-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    -moz-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    -khtml-box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    box-shadow: 1px 1px 3px rgba(0,0,0, 0.25);
    margin-right: -22px !important;
    text-transform: uppercase;
    margin-right: -25px;
}
.message .newIndicator span {
    background-color: rgb(255, 60, 0);
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    position: absolute;
    top: -4px;
    right: -1px;
    width: 5px;
    height: 4px;
}
/*Link*/
.ugc a:link, .ugc a:visited {
    color: rgb(255, 141, 0);
    padding: 0 3px;
    margin: 0 -3px;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
}
/*Link Hover*/
.ugc a:hover, .ugc a:focus {
    color: rgb(255, 60, 0);
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    -khtml-box-shadow: none;
    box-shadow: none;
}
/*Quote*/
.bbCodeBlock .type {
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 141, 0);
    background-color: rgb(26, 26, 26);
    padding: 7px 8px;
    margin-bottom: 5px;
