style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://steamcommunity.com/chat") {
  
  .chat_page {
    padding: 0 0 0 0;
    min-width: 0;
  }
  
  .chat_page_header {
    background-color: #262627;
    padding: 10px;
  }
  
  .btn_chat_settings {
    position: relative;
    right: 10px;
    border: none;
    border-radius: 0;
    box-shadow: none;
  }
  
  .maincontent {
    max-width: 100%;
  }
  
  #chat_friendslist_area {
    background: none;
    background-color: #262627;
    border: none;
    box-shadow: none;
  }
  
  .friendslist_group_handle {
    background-image: none;
    background: rgba(0, 0, 0, 0.1);
  }
  
  .friendslist_entry:hover {
    background-image: none;
    background: rgba(0, 0, 0, 0.2);
  }
  
  .unread_message_count {
    background-image: none !important;
    background-color: #40BCDE;
    padding: 0 !important;;
    width: 25px !important;
    height: 25px !important;
    border-radius: 50%;
  }
  
  .unread_message_count_value {
    color: white;
    text-shadow: none;
    line-height: 25px;
  }
  
  #chat_msg_area {
    border: none;
    background: none;
    box-shadow: none;
  }
  
  .chat_msg_area_highlight {
    height: 0px;
  }
  
  .chat_dialog_scroll {
    background: rgba(0, 0, 0, 0.2);
  }
  
  .chat_dialog_content {
    background: none;
    border-style: none;
  }
  
  .chatform_textarea {
    border: none;
    box-shadow: none;
    border-radius: 0;
  }
  
  .chatform_footer button {
    border: 0;
  }
  
  .btn_darkblue_white_innerfade {
    border: 0;
    border-radius: 0;
    padding: 0;
    background: #225D7E;
  }
  
  .btn_darkblue_white_innerfade:hover, .btn_darkblue_white_innerfade > span:hover {
    background: none;
    background-color: #4EA8DB;
  }
  
  .btn_darkblue_white_innerfade > span {
    background: none;
  }
}
