style.css:
@-moz-document url-prefix("https://tbsplay.ru/") {
#header>div {margin-top: 15px;  margin-bottom: 15px;}
#userBar .navTabs {height: 40px; background: rgb(40, 46, 58);}
#userBar .navTabs .navLink, #userBar .navTabs .SplitCtrl {line-height: 38px;  height: 40px;  padding-top: 2px;}
#userBar .navTabs .navTab.Popup.PopupOpen, 
#userBar .navTabs .navTab.selected.PopupOpen .navLink {background: rgb(47, 77, 138);}

#userBar.activeSticky .navTabs .navLink, #userBar.activeSticky .navTabs .SplitCtrl {height: 40px;  line-height: 38px;  padding-top: 2px;}
#userBar.activeSticky .navTabs {height: 40px;}

.Menu {border-top: 2px solid rgb(47, 77, 138);}

.navTabs {height: 50px; background: rgb(40, 46, 58);}
.navTabs .navTab.uix_searchTab {background: rgb(40, 46, 58);}
.navTabs .navLink, .navTabs .SplitCtrl {height: 50px;  line-height: 48px;  padding-top: 2px;}
#searchBar, #uix_searchMinimal form {  line-height: 50px;  height: 50px;}

.hasTabLinks #navigation .navTabs .navTab.selected .navLink {background: rgb(47, 77, 138);}
.navTabs .navTab.Popup.PopupOpen, .navTabs .navTab.selected.PopupOpen .navLink {background: rgb(47, 77, 138);}

#logoBlock {display: none;}
.ctaFtBlock {display: none;}

.footer .pageContent {background: rgb(40, 46, 58); margin-top: 15px;  margin-bottom: 15px;   line-height: 38px; padding-top: 2px;}
.footerLinks, .topLink a {  line-height: 30px;  margin-top: 2px;}
.footer .choosers a {background: rgb(40, 46, 58);}
.footerLinks {background: rgb(40, 46, 58);}

#navigation.activeSticky.lastSticky, #userBar.activeSticky.lastSticky, .activeSticky.lastSticky .sticky_wrapper .uix_navigationWrapper {margin-top: -10px;}

.message .messageMeta {background-color: rgb(40, 46, 58);}
.message .messageMeta .control {line-height: 30px; height: 30px;}

.xenOverlay {display: none;}
#exposeMask {display: none;}
}
