style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mg.mail.yahoo.com") {
  /*--------------------------------------YAHOO DESIGN >=juni 2017--------------*/
  [data-test-id="mail-right-rail"] > div:nth-child(2) {
    display: none !important;
  }
  [data-test-id="mail-right-rail"] {
    width: 50px !important;
  }
  [data-test-id="mail-right-rail"] > div:nth-child(1) {
    height: 180px !important;
    flex-direction: column !important;
  }
  [data-test-id="mail-right-rail"] > div:nth-child(1) > div:nth-child(1) {
    flex-direction: column !important;
    
  }
  [data-test-id="mail-right-rail"] > div:nth-child(1) > div:nth-child(1) > a {
    padding-bottom: 20px;
  }
  [data-test-id="settings-link-label"] {
    display: none !important;
  }
  
  [data-test-id="pencil-ad"] {
    display: none !important;
  }
}




@-moz-document domain("mail.yahoo.com"){
  
  
  /*--------------------------------------YAHOO DESIGN < juni 2017--------------*/
  /*removee adds on right side*/
  #shellcontent{
    right:0!important
  }

  #theAd, #theMNWAd, #slot_MON, #slot_LREC, #slot_MIP, #slot_MON_sent, .darla, .ch_mod, .gxBoom, .boom, #trendmastr-main, #masSearchAd, #slot_MB, #slot_mbrec, .mb-msg-read-img-ad, #mobilizer {
    display:none!important;
    visibility:hidden!important;
  }

  .panescroll #toolbar {
     right:0 !important;
  }

  .pc.panescroll #shellnavigation, .pc.panescroll #theAd, .pc.panescroll #shellcontent {
     top: 0px !important;
  }

  #main, #yucs, #yuhead-bucket {
     max-width: 2560px !important;
  }
  
  
  
  /*top bar with links removed*/
  #masterNav, #yucs-top-bar {
    display: none !important;
  }

  #uhWrapper {
    top: 5px; 
  }

  .pc.uh3 #main {
      top: 50px!important;
  }

  #menu-move-folder {
      max-height: 375px !important; 
  }
  /*make larger mail selection*/
  #shellnavigation {
    width: 160px;
    transition: width 0.3s, background-color 0.3s;
    overflow:hidden;
    background-color: rgba(255,255,255,0);
  }
  #shellcontent{
    position: fixed;
    top: 50px;
    left: 160px;
  }
  
  /*no Icons*/
  #nav {
    display: none !important;
  }
  
 /*no text on icons*/ 
  #uhNavWrapper b{
    font-size: 0 !important;
  }
  #uhNavWrapper i {
    margin: 0 !important;
  }
  #uhNavWrapper li {
    margin-right: 5px !important;
  }
  
  /*no search the web button*/
  #web-search-btn {
    display: none !important;
  }
  
  /*no adds in mails*/
  .ml-bg, .mb-native-ad, -mb-native-ad > * {
    position: fixed; 
    top: 100% !important;
    left: 100% !important;
    display: none !important;
    height: 0 !important;
    font-size: 0 !important;
  }
}





