style.css:
@-moz-document domain("squidboards.com") {
.messageList .messageUserInfo a.avatar img, .quickReply .messageUserInfo a.avatar img, .conversation_view .messageList .messageUserInfo a.avatar img, .conversation_view .quickReply .messageUserInfo a.avatar img {
    width: 210px;
    height: auto;
}

div .message .signature {
    font-size: 9pt;
    color: rgb(93,93,93);
    padding-top: 6px;
    padding-bottom: 6px;
    margin-top: 6px;
    margin-bottom: 6px;
    border: 0 dashed rgb(22,22,22);
    border-top-width: 1px;
    border-bottom-width: 1px;
    max-height: /*[[bigsig]]*/;
    overflow: hidden;
}

div .messageUserBlock a.username {
font-weight: bold;
display: block;
overflow: hidden;
line-height: 16px;
color: /*[[namecolor]]*/;
text-shadow: 0px 0 4px /*[[nameglow]]*/;
}

div .message {
/*[[postinkcolor]]*/
}
}

