@-moz-document domain("habitica.com") {

  /*------------------------------ SITE HEADER (MENUS & AVATAR) ------------------------------ */
  
    /* toolbar fix for firefox users using the vertical scrolling bar on the left of the screen */
    .toolbar {
      right: unset !important;
    }
    .toolbar-button > a > span, .toolbar-button-dropdown > a > span {text-transform: uppercase !important;}
  
    .toolbar-nav { margin-left: 0.5em; }
    .toolbar-settings { margin-right: 0.5em; }
  
    #main .col-md-12 {
      padding-left: 0;
    }
    
    /* we add the logo (original height="61" width="240") */
    .toolbar-container::before {
      float: left;
      content: "";
      background-color: #402971 !important;
      background: url('https://i.imgur.com/sd7WKVE.png') no-repeat; /* XiRKyrN sd7WKVE */
      background-size: auto 100%;
      background-position: 10px;
      width: 140px;
      height: 30px;
      padding: 0 3em 0 0.3em !important;
    }
    .toolbar-wallet, .toolbar-button a, .toolbar-button-dropdown a, .toolbar-options a {
      height: 30px !important;
    }
      
    .toolbar-wallet .gem-wallet span {
      padding-top: 0;
    }
    
    /* we adjust the paddings & margins to be perfectly aligned with the logo - default: 0.25em */
    .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button-dropdown > a {
      padding-top: 0.3em !important;
      padding-bottom: 0.2em !important;
    }
    
    .toolbar-options a span { margin-top: 3px !important; }
    .toolbar-wallet li  { margin-top: 4px !important; }
  
    /* the wrapper height has to reflect the toolbar height */
    .header-wrap {
      padding-top: 28px;
    }
  
    /* we reduce the height of the toolbar */
    .toolbar-nav, .toolbar-bailey, .toolbar-options, .toolbar-subscribe, .toolbar-wallet {
      padding: 0;
    }
    
    .toolbar-currency {
      margin-bottom: 0;
    }
      
    /* we remove the buttons border */
    .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button-dropdown {
      border-width: 0 1px 0 0 !important;
    }
  
     .toolbar-options > li > a {
      border-width: 0 !important;
    }
  
    .toolbar-subscribe-button > button {
      border-width: 0 0 0 1px !important;
    }
    
    /* we remove the space between buttons */
    .toolbar-options > li:not(:first-of-type) {
      margin-left: 0;
    }
    .toolbar-nav > li {
      margin-right: 0;
    }
       
    /* we remove the background of the avatar level & name */
    .herobox .avatar-level, .herobox .avatar-name {
      border: 0;
      outline: 0;
      background: none !important;
    }
    
    /* we update the background color of the avatar on hover */
    .herobox.noBackgroundImage:hover {
      background-color: #C9DAF8;
    }
   
    /* what follows impacts only the submenus */
  
    /* we remove the light border before the sandwitch icon */
    .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
      border-left: none !important;
    }
    

    /* we replace the existing 'triangle' on the top of each submenu, which is actually a square, by an actual triangle */
    .toolbar-button-dropdown .toolbar-submenu::before, .toolbar-button-dropdown .toolbar-submenu::after {
      transform: rotate(0deg) !important;
      content: " " !important;
      position: absolute !important;
      width: 0 !important;
      height: 0 !important;
      border-style: solid !important;
      border-color: transparent !important;
      border-top: none !important;
      background: transparent !important;
    }

    .toolbar-button-dropdown .toolbar-submenu::before {
      border-bottom-color: #402971 !important;
      border-width: 12px !important;
      top: -12px !important;
      left: 10px !important;
    }
  
    .toolbar-button-dropdown  .toolbar-submenu::after {
      border-bottom-color: #fff !important;
      border-width: 11px !important;
      top: -11px !important;
      left: 11px !important;
      z-index: 2 !important;
    }
      
    /* we add a amsll padding top because the triangle overlaps the menu border, so it shows when the first item is hovered */
    .toolbar-submenu { padding-top: 2px !important; }
    
    /* we change the border color so it matches the color of the hovered items */
    .toolbar-nav .toolbar-button-dropdown > div, .toolbar-audio > div, .toolbar-notifs > div, .toolbar-settings > div  {
      border-color: #402971 !important; /* default: #C6D6D6 */
    }
    
    
    /* the triangle being removed, we unset the top so the submenu appears just below its sandwitch button, without gaps in between. the margin trick allows a perfect alignment with the navbar borders */
    .toolbar-audio > div, .toolbar-mobile > div, .toolbar-nav .toolbar-button-dropdown > div, .toolbar-notifs > div, .toolbar-settings > div {
      top: 2.6em !important;
      margin-left: -1px;
      margin-right: -1px;
      
    }
        
    /* we reduce the padding for a more condensed look */
    .toolbar-audio > div ul, .toolbar-mobile > div ul, .toolbar-nav .toolbar-button-dropdown > div ul, .toolbar-notifs > div ul, .toolbar-settings > div ul {
      padding-top: 0;
      padding-bottom: 0;
    }

    /*.toolbar-audio > div ul a, .toolbar-mobile > div ul a, .toolbar-nav .toolbar-button-dropdown > div ul a, .toolbar-notifs > div ul a, .toolbar-settings > div ul a {
      padding: 0.5em 1.2em 0.5em 0.5em;*/ /* default: 0.618em 1em 
    }*/
  
    /* we remove the top arrow then remove the top margin so the popover appears like the other submenus */
    .popover.bottom > .arrow, .popover.bottom > .arrow:after { display: none !important; }

    .popover.bottom{
      margin-left: -30px !important; /* blob to display it completely now that the button is fully to the right */
      margin-top: 0; 
    }
    
    /* new */
    .toolbar, .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button-dropdown.highlight, .toolbar-nav .toolbar-button-dropdown.highlight > a:nth-of-type(2) {
      border: none !important;    
    }


    .toolbar-audio > div, .toolbar-mobile > div, .toolbar-nav .toolbar-button-dropdown > div, .toolbar-notifs > div, .toolbar-settings > div {
      border-color: #9ea9ab;
    }
  
    .toolbar-nav .toolbar-button-dropdown {
      border-color: #402971 !important;
    }
      
    .toolbar, .toolbar-nav .toolbar-button-dropdown.highlight textarea, .toolbar-nav .toolbar-button-dropdown.highlight > a, .toolbar-nav .toolbar-button-dropdown.highlight > button, .toolbar-nav .toolbar-button-dropdown.highlight > input , .toolbar-nav .toolbar-button textarea, .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button > button, .toolbar-nav .toolbar-button > input, .toolbar-nav .toolbar-button-dropdown textarea,    .toolbar-nav .toolbar-button-dropdown > a, .toolbar-nav .toolbar-button-dropdown > button, .toolbar-nav .toolbar-button-dropdown > input, .toolbar-audio textarea, .toolbar-audio > a, .toolbar-audio > button, .toolbar-audio > input, .toolbar-settings textarea, .toolbar-settings > a, .toolbar-settings > button, .toolbar-settings > input, .toolbar-sync textarea, .toolbar-sync > a, .toolbar-sync > button, .toolbar-sync > input, .toolbar-notifs textarea, .toolbar-notifs > a, .toolbar-notifs > button, .toolbar-notifs > input {
      background-color: #402971 !important;
      color: #c7bfd6 !important; 
    }
    
    .toolbar-notifs .glyphicon, .toolbar-audio .glyphicon, .toolbar-settings .glyphicon, .toolbar-sync .glyphicon {
      color: #c7bfd6 !important;
    }
        
    /* we make the active menu more visible */
    .toolbar-nav .toolbar-button-dropdown > a.active, .toolbar-nav .toolbar-button-dropdown.highlight > a.active {
      background-color: #36205d !important;
      border-bottom: 2px solid #ffffff !important;
      color: #fff !important;
    }
    .toolbar-nav .toolbar-button-dropdown > a.active span, .toolbar-nav .toolbar-button-dropdown.highlight > a.active span {
      color: #fff !important;
    }
    
    /* we make the different options of the submenu more visible */
    .toolbar-mobile a:hover, .toolbar-nav .toolbar-button a:hover, .toolbar-nav .toolbar-button-dropdown a:hover, .toolbar-options > li > a:hover, .toolbar-nav .toolbar-button-dropdown.highlight a:hover {
      background: none !important;
      background-color: #402971 !important;
      color: #ffffff !important;
      border-bottom: 2px solid #ffffff !important;
    }
    
    .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2):hover > span::before, .toolbar-options a:hover span {
      color: #fff !important;
      border-top-color: #fff !important;
    }
      
    /* see http://benkaminski.com/2014/08/30/how-to-customize-bootstrap-3-popovers-individually-using-only-css/ for more details about bootstrap popover styling */
    .popover {
      border-radius: 0;
      border-color: #9ea9ab;
      -webkit-box-shadow: none !important;
              box-shadow: none !important;
    }
    
    /* we modify the toolbar submenu */
    .toolbar-submenu li a, .toolbar-notifs-notifs li a {
      padding-top: 0.2em !important;
      padding-bottom: 0.05em !important;
    }
      
    .toolbar-submenu li:hover a, .toolbar-notifs-notifs li:hover, .toolbar-notifs-notifs li:hover a {
      text-decoration: none !important;
      background-color: #402971 !important;
      color: #fff !important;
    }
  
    .toolbar-submenu li:hover a:link, .toolbar-submenu li:hover a:visited {
      color: #fff !important;
    }
    
    /* focus on the notifications */
    .toolbar-notifs  div {
      padding-bottom: 0; /* defaul: 1em */
    }


}

@-moz-document domain("habitica.com") {

  /*------------------------------ SITE HEADER (STAT BARS) ------------------------------ */
  /* we remove the gap between symbol & bar then tweak the colors & round the edges */
  /* we also replace the grey background by a light blue one */
   
    /* coupled with the margin-bottom: 0 of the last bar (mana), we have a nicely centered stat bars area (works only after level 10 though, when the mana bar appears) */
    .hero-stats {
      padding-top: 0;
      padding-left: 0;
      padding-right: 1.5em;
      border-right: none !important;
    }
  
    .hero-stats .meter-label {
      margin-right: 0; /* default: 0.618em*/  
      text-shadow: unset !important;
      outline: 0;
      height: 1.9em;
      width: 3em;
      padding: 0.3em 0 0.5em 0.4em;
    }
  
    .hero-stats .meter {
      outline: 0;
      height: 1.618em; /* default: 1.618em */
      margin-bottom: 0.6em;
    }
  
    .hero-stats .meter-text.value {
      right: 0.75em;
    }
  
    /* new */
   .header-wrap, header .hero-stats, .herobox.noBackgroundImage {
      background: #402971;
    }
  
    .hero-stats .meter-label {
      border: none !important;
      outline: none !important;
      background: none !important;
      font-size: 1em !important;
    }
  
    .hero-stats .meter {
      border: none !important;
      background: #36205d !important; 
      color: #fff;
      font-weight: bold;
    }
    .hero-stats .meter, .hero-stats .meter > .bar {
      border-radius: 0.5em !important;
    }
  
    .hero-stats .meter-label[tooltip='Health'] {color: #ff6165;}
    .hero-stats .meter-label[tooltip='Experience'] {color: #ffbe5d;}
    .hero-stats .meter-label[tooltip='Mana'] {color: #50b5e9;}
    
    .hero-stats .meter.health > .bar {background: #ff6165;}
    .hero-stats .meter.experience > .bar {background: #ffbe5d;}
    .hero-stats .meter.mana > .bar {background: #50b5e9;}
    

  	/* we put a white background to the general graph located in the xp bar */
  	.col-md-12.exp-chart {
      background: #FFFFFF !important;
      border-bottom: 1px solid #C3D1D6;
      margin-bottom: -30px;
    }
  
   /* we resize the graph so everything is displayed properly and reposition it so it shows below the menus & we change the glyphicon color in the exp bar */  
   .hero-stats .meter.experience .meter-text a {color: #fff !important;}
   .col-md-12.exp-chart > div > div:first-of-type { height: 320px !important; }
   .col-md-12.exp-chart > div > div:first-of-type > div:first-of-type { top: 0 !important; }
   .col-md-12.exp-chart > div > div:first-of-type > div:first-of-type > svg { height: 320px !important; width: 350px !important; }
            
}

@-moz-document domain("habitica.com") {

  /*------------------------------ TASKS FILTER TAGS ------------------------------ */
    
    /* body bg */
    body {
      /*background-color: #c7bfd6;*/
      /*background-color: #C3D1D6;*/
      /*background-color: #e2e4e6; e9ebed e9eaed f1f1f1 f0f1f2 f0f1f5*/
      background-color: #e9ebed;
    }
  
    /* we remove the gap between the header and the filter tags (note: we cannot unset the height because if we do so, the scrolling will snap to the sticky wrapper and yield unwanted effects */
    .sticky-wrapper {
      height: calc(147px + 28px) !important; /* height of the avatar picture + height of the navbar */
    }
      
    #wrap {
      min-height: unset !important;
      padding-bottom: 0.618em;
      border-top: 1px solid #402971;
    }
  
    /* we improve the centering of tag labels */
    .filters .filters-controls li:first-of-type, .filters .filters-controls li > a, .filters .filters-tags > a {
      padding-top: 0.3em !important; /* default  0.25em */
    }
    
}

@-moz-document domain("habitica.com") {

  /*------------------------------ TASKS COLUMNS LAYOUT ------------------------------ */
    
    .task-column h2 {
      color: #402971;
    }
  
    .tasks-lists {
      /*border-top: 1px solid #c7bfd6;*/
      padding-left: 1em;
      padding-right: 1em;
    }
      
    .tasks-lists .row .col-md-3 {
      padding: 0;
      margin: 0.618em 0;
    }
    
    /* we use the -1px trick instead of keeping only the left (or right) border in order to have a nice border going all the way down the lists of tasks */
    .task-column {
      padding: 0.5em 1.5em !important;
      background: none !important;
      border-width: 0;
      border-color: #c7bfd6;
      margin-left: 0; 
    }


    /* we hide the always empty items rewards ul of all columns except for rewards */
    .task-column:not(.rewards) .items.rewards, .task-column.rewards .items.rewards:not(.ng-scope) {
      display: none !important;
    }
    
    /* we update the title margin for a more compact look */
    .task-column .task-column_title {
      margin: 0;
      text-align: center;
    }
    
    /* we tweak the option box, which are the icons on the right of the column title */
    .option-box {
      margin: 0.75em 0.1em 0 0;
    }
  
    /* we bring the add box closer to the title */
    .task-add {
      margin-top: 1em;  /* default: 1.5em */
    }
      
    /* we bring the task filter closer to the add box + we make the unselected options less visible (same as add) */
    .task-filter {
      margin-top: 1em;    /* default 1.5 em */
      margin-bottom: 0; /* default: 1em; we use .main-list instead */
      border: none !important;
    }
    .task-filter li {
      border: none !important;
      border-top: 1px solid #e9ebed !important;
      border-bottom: 1px solid #e9ebed !important;
    }
    .task-filter li a {
      color: #c7bfd6 !important; 
      background-color: #e9ebed !important;
      padding: 0;
    }
    
    .task-filter li.active {
      border: 1px solid #dedede !important;
      background-color: #e9ebed !important;
    }
    .task-filter li.active a {
      box-shadow: none !important;
      background-color: #e9ebed !important;
    }
    
    .task-filter:hover li a {
      color: #402971 !important;
    }
    .task-filter:hover li.active {
      border-color: #402971 !important;
    }
    .task-filter:hover li.active a, .task-filter li:hover a {
      background-color: #fff !important;
    }
  
    /* using main list margin, so it still looks good when filters are not displayed */
    .main-list, .task-column.rewards .items {
      margin-top: 1em;
      clear: both;
      /*border: 1px solid #ccc;*/
      /*box-shadow: 0 0 2px 0 #ccc;*/      
    }
}

@-moz-document domain("habitica.com") {

  /*------------------------------ TASKS COLUMNS - HEADERS ------------------------------ */
  
    /* default for input bars is 2.618em, so we use that to square the '+' button */
    .task-add { margin-bottom: 0 !important; }
    .task-add input, .task-add textarea { margin-bottom: 0; }
    .task-add input, .task-add textarea { 
      width: 100%;
      padding-left: 1em !important; 
      padding-right: calc(2.618em + 1em) !important; 
      line-height: 1 !important;
    }
    .task-add textarea {
      padding-top: 0.7em !important;
      padding-bottom: 0.25em !important;
    }
  
    .task-add > button { 
      position: absolute;
      top: 0;
      right: 0;
      width: 2.618em; 
      /*padding-top: 0.45em !important;*/ /* we adjust the button padding so it looks properly centered */
      border: 1px solid transparent !important;
      border-left: none !important;
    }
    
    .task-add input, .task-add textarea, .task-add > button {
      background-color: #e9ebed !important;
    }
        
    /* we adjust both input & button styles, so they are more discreet when not hovered or focused */
    .task-add input, .task-add textarea, .task-add button { 
      border-color: #dedede !important; 
      color: #c7bfd6 !important; 
    }
    .task-add > button span { color: #ddd4ed !important; }
    
    /* we update the display when hovered or focused. In particular, we handle the button display when the input in focused */
    .task-add:hover input, .task-add:hover textarea, .task-add:hover button {
      color: black !important;
      border-color: #bdbdbd !important;
      background-color: #fff !important;
    } 
    
  
    .task-add input:focus, .task-add input:focus + button, .task-add input.ng-valid, 
    .task-add textarea:focus, .task-add textarea:focus + button, .task-add textarea.ng-valid {
      color: black !important;
      border-color: #402971 !important;
      background-color: #fff !important;
    }
    
  
    /* we update the button bg when it is hovered or when the input is focused */
    .task-add input.ng-valid + button { background-color: #402971 !important; border-color: #402971 !important;} 
    .task-add input.ng-valid + button:hover span { color: #fff !important; }
    .task-add textarea.ng-valid + button { background-color: #402971 !important; } 
    .task-add textarea.ng-valid + button:hover span { color: #fff !important; border-color: #402971 !important;}
  
    /*402971 c7bfd6 36205d*/
    
    /* we bring the 'add multiple' button closer to the input bar */
    .help-block.btn-link {
      margin-top: 2px; /* default: 5px */
      margin-bottom: 0;
      color: #c7bfd6;
    }
   
    /* we change the color of 'Add multiple' on hover & focus */
    .task-add input:focus .help-block.btn-link, .task-add:hover .help-block.btn-link {
      color: #402971;
    }
    
    /* we add an hover option for the option box, where graphs & explanations icons are shown, so it is more discreet */
    .task-column > .option-box span { 
      color: #c7bfd6 !important;
    }
    
    .task-column > .option-box span:hover { 
      color: #000 !important;
    }
  
}

@-moz-document domain("habitica.com") {

  /*------------------------------ TASKS COLUMNS - TASKS ------------------------------ */
  /* we reduce the size of the colored band of each task, then update text margins accordingly */
    
    /* we bring tasks closer from one another */
    .task {
      margin-bottom: 2px;  /* default: 0.75em */
      /*font-size: 16px;*/
    }
    
  
    /* we use shadow to create a seamless border */
    .task, .task-options {
      border: none !important;
      box-shadow: none !important;
      /*box-shadow: inset 0 0 0 1px rgba(0,0,0,0.07) !important;*/
      /*box-shadow: inset -1px -1px 1px -1px rgba(0,0,0,0.25) !important;*/
    }
  
    /* the reward column min height is left untouched because otherwise we would have to resize all images which is a real pain */
    .task-column:not(.rewards) .task { 
      min-height: 2.5em !important;  /*default: 3em */
    }
    
    /* we reduce the width of the lateral band of each task, to match the modified min-height */
    .habits .task-actions, .task-checker label {
      width: 2.5em; /*default: 3em */
    }
    
    /* we update the checkbox position */
    .action-yesno label::after { 
      margin: 13px auto; 
      opacity: 0.4;
    }

    /* we resize the dailies & todos success check to match the resized box */
    .action-yesno label::after, .action-yesno label::before {
      font-size: 1.38em;
    }
    
    /* we */
    .task-column:not(.rewards) .task-checklist {
      margin-right: 0;
    }
  
    /* we update the text position, except for rewards which are left untouched */
    .task-column:not(.rewards) .task-text {
      padding: 0.35em 0.7em 0.3em 3.2em; /* default: 0.75em 0px 0.75em 3.75em */
      line-height: 1.6;
      color: #333;
    }
    
    /* we update the controls position, except for rewards which are left untouched (note that they appear only for custom rewards) */
    .task-column:not(.rewards) .task-meta-controls {
      margin: 0.5em 0.5em 0px;
    }
    
    /* hide task icons when not hovered for a cleaner look, except for the number of taks & the icon indicating there is a note */
    .task-meta-controls > span > a:not(.badge) {display: none !important;}
    .task:not(.reward):not(.todo):hover .task-meta-controls > span > a:not(.badge) {
      display: inline !important;
    }
