@-moz-document domain("habitica.com") {

  /*------------------------------ OTHER PAGES - INVENTORY ------------------------------ */
    
    .pets-menu .customize-option, .inventory-list .customize-option, .customize-option.inventory_quest_scroll, .customize-option.inventory_quest_scroll_locked {
      transition: none !important;
    }
  
    /* hide sub menu 'quests' in the market */
    .inventory-list .pets-menu[label='Quests'] {display: none !important;}
    /* we update the position of the small descriptive paragraphs with a break using order, and we let them breeze a little more */
    .pets-menu > p {
      margin-top: -3px;
      margin-bottom: 0.5em;
      order: 2;
      width: 100%;
    }
    
  
    /* pets & potions menu - flexed to position its content more easily  */
    .pets-menu > div, .hatchingPotion-menu > div {
      display: flex;
      flex-direction: column-reverse; /* show gems cost below, not after the market items */
      justify-content: flex-end;      /* we position the content at the top of the div */
      align-items: center;
      margin: 0;
      padding: 0;
      height: 70px; /* a bit bigger than the button's bg img, so we can play with the button's border to fake a padding (70px + 22px actually; the extra 20px are to display the gem cost when relevant with 2px for the top margin */
      width: 70px;  /* a bit bigger than the button's bg img, so we can play with the button's border to fake a padding */
      order: 2;  /* same as before: we create a break after the menu's after pseudo element (see css for user pages */
    }
    
    .npc_alex_container + menu .pets-menu > div, .npc_alex_container + menu .hatchingPotion-menu > div,
    .pets-menu[label='Unlockable Quests'] > div, .pets-menu[label='Gold Purchasable Quests'] > div, .pets-menu[label='Pet and Mount Quests'] > div,
    .stable + li .pets-menu > div {
      height: 95px;
    }
  
    /* we increase the width of the special menu, as icon there are larger */
    /*.pets-menu[label='Special'] > div {
      width: 79px;
    }*/
  
    /* we exclude the border from the dimensions taken into account, to let the default dimensions untouched */
    .pets-menu > div > button, .hatchingPotion-menu > div > button {
      box-sizing: content-box;
      border-width: 10px 11px; /* 70px minus 51px & minus 48px, both divided by 2 */
      border-color: #EDEDED;   /* same as the button's bg color */
      padding: 0;
    }
    
    /* specific dase for locked quests & items */
    .customize-menu .locked {
        border-color: #727272;
    }
    .customize-menu .locked ~ p {
        background-color: #505050 !important;
        color: #b3b3b3;
    }
    
    /* special case for inventory gear (for which the imgs are smaller: 40x40) */
    .inventory-gear .pets-menu > div > button, .checkbox.equipment-title ~ li.customize-menu .pets-menu > div > button {
      border-width: 15px 15px;
    }
  
    /* as we have changed the buttons' size, we tweak the stack-count badge accordingly */
    .stack-count {
      position: absolute;
      bottom: -10px !important;
      right: -11px !important;
    }
     
    /* small cosmetics improvements for the count badge */
    .pets-menu .badge, .hatchingPotion-menu .badge {
      padding: 3px 5px;
      border-radius: 0;
    }
   
    /* different markup for the seasonal items so we are more precise for these */
    .pets-menu > div p, .inventory-gear .pets-menu > div > button + div, .checkbox.equipment-title ~ li.customize-menu .pets-menu > div > button + div {
      width: 100%;
      padding-top: 5px;
      background: #E3E3E3 !important;
    }
    .pets-menu > div p > .inline-gems{
      margin-left: 2px;
      margin-top: -4px;
    }
    
    /* specific case for time travellers & seasonal shop*/
    .col-md-12 > .inventory-gear menu {
      display: inline-block !important;
      margin-right: 2em;
    }
    .col-md-12 > .inventory-gear menu > div {
      display: inline-block !important;
    }
    .col-md-12 > .inventory-gear menu::before {
      display: block;
    }
    .col-md-12 > .inventory-gear menu::after {
      content: none;
    }

    /*
    .customize-option:not(.selectableInventory):hover + p, .customize-option:not(.selectableInventory):hover + div {
      background-color: #C0D4F7 !important;
      border-color: #C0D4F7 !important;
      transition: all 0.5s ease-out !important;
      color: #333;
    } 
    */
    
    /* we change the color of the selected button, using the same color as the help button hover */   
    .customize-option.selectableInventory, .pet-button.selectableInventory {
      border-radius: 0;
      background-color: #C0D4F7 !important;
      border-color: #C0D4F7 !important;
    }
  
/*------------------------------ OTHER PAGES - PETS ------------------------------ */
  
    /* pets progress bar */
    menu.pets .customize-menu .progress {
      width: 70%;
      left: 15%;
      margin-bottom: 19px !important; /* default: 20px */
      box-shadow: none !important;
      border: 1px solid #4781e7;
    }
    menu.pets .customize-menu .progress .progress-bar-success {
      background-color: #4781e7;
    }
   
    /* we make the food tray larger and we remove the label of the food */
    .food-tray {
      max-width: unset !important;
      padding: 20px !important;
      top: 50%;
      bottom: unset !important;
    }
  
    .food-tray .pets-menu[label="Food and Saddles"] > div { height: 70px !important; }
    .food-tray .pets-menu[label="Food and Saddles"] > div > p { display: none !important; }
  
  
  /*------------------------------ OTHER PAGES - SOCIAL ------------------------------ */
   
    .options-submenu {
      margin-top: 1em;
    }
    .options-submenu + div .options-blurbmenu {
      margin-top: 2em !important;
    }
    .options-blurbmenu {
      margin-top: 0em;
    }
    
    .col-md-8, .tavern, .tavern-chat {
      background-color: #fff !important;
    }
   
    /* tasks in challenge */
    .col-md-10 habitrpg-tasks[obj='challenge'] > div > div {
      background-color: #e9ebed;
    }  
}

