*@Все права защищены! :)                        *
*************************************************
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("last.fm") {
  html, body {background: url(/*[[Images]]*/) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important; color: /*[[text_color]]*/ !important;}
  body::before {background: none !important;}
  a {color: /*[[Color_lnk]]*/ !important;}
  h1, .h1, h3, .h3, h4, .h4 {color: /*[[text_color]]*/ !important;}
  /*Заголовок*/
  .header {color: /*[[text_color]]*/ !important;}
  .header-scrobble-since {color: /*[[text_color2]]*/ !important;}
  .metadata-title {color: /*[[text_color2]]*/ !important;}
  .metadata-display {color: /*[[text_color2]]*/ !important;}
  .nag-bar, .alert:first-child {/*[[Sys]]*/}
  .two-column-layout .content-top {background: none !important;}
  .header .header-background {/*[[header]]*/}
  .player-bar {/*[[play]]*/}
  .top-bar {background: none !important;}
  .header {background: url(/*[[shapka]]*/) no-repeat; background-size: 100% 100%;}
  .dropdown:hover .dropdown-menu, .dropdown:focus .dropdown-menu, .dropdown .dropdown-menu-item:hover, .dropdown .dropdown-menu-item:focus, .dropdown-menu.dropdown--open {background: /*[[menu_fon_color]]*/ !important;}
  /*Поиск*/
  .masthead .search-form .search-field {border-color: /*[[search]]*/ !important; color: /*[[search]]*/ !important;}
   /********************/
  /*Контент*/
  .container {background: none !important;}
  .two-column-layout .content-top .container {background: none !important;}
  .content-top::after, .content-top .content-top-lower::after {border: none !important;}
  .stationlinks {color: /*[[Color_lnk]]*/ !important;}
  .stationlink-list li {box-shadow: none !important;}
  .chartlist tr:nth-child(2n) td {background: none !important;}
  .js-link-block {box-shadow: none !important;}
  .chartlist td, .chartlist th {box-shadow: none !important;}
  .comments .media .text-container .comment-text {color: white !important;}
  .chartlist .countbar-bar-slug {background-color: /*[[menu_fon_color]]*/ !important;}
  .recs-feed .context {background: none !important; box-shadow: none !important;}
  .chartlist .now-scrobbling {/*[[scrobl]]*/}
  .avatar {border-radius: /*[[radius]]*/%;}
  .feedback-message {/*[[feedback]]*/}
  .chartlist .chartlist-index, .chartlist .chartlist-artists, .chartlist .chartlist-album, .chartlist .artist-name-spacer, .chartlist .chartlist-duration, .chartlist .chartlist-timestamp, .chartlist .chartlist-countbar, .comments .media .text-container .timestamp {color: /*[[time_color]]*/ !important;}
  .grid-items-cover-image {background-image: linear-gradient(to bottom, transparent 25%, /*[[podsvetka]]*/ 100%);}
  .footer-wrapper .container .footer {color: /*[[text_color]]*/ !important;}
  /********************/
  /*Плеер*/
  .player-bar.inactive .player-bar-btn {opacity: 1 !important;}
  .player-bar .player-bar-progress {background: /*[[menu_fon_color]]*/ !important;}
  /*[[player_buttons]]*/
  /**/
  /*Виджет*/
  .kerve.widget {background: none !important; border: none !important;}
  .yad-widget li {border: none !important; border-bottom: 0px !important;}
  .features-promoted .features-post-title {color: /*[[text_color]]*/ !important;}
  /********************/
  /*Сообщения*/
  .inbox .inbox-message--unviewed {background: none !important; border: none !important;}
  .inbox .inbox-buttons {border: none !important;}
  .inbox tr, .inbox td {box-shadow: none !important;}
  /********************/
  /**/
  .comments .media .text-container .comment-text {color: /*[[comm_color]]*/ !important;}
  /********************/
  /*Настройки*/
  .section-settings {background: none !important; border: none !important;}
  .section-settings-toggle {border: none !important;}
  .selectbox .selectboxit-btn {color: /*[[Color_lnk]]*/ !important;}
  .selectbox .selectboxit-options {background: /*[[menu_fon_color]]*/ !important; border: none !important;}
  .selectbox .selectboxit-focus .selectboxit-option-anchor {background: /*[[menu_fon_color]]*/ !important;}
  /**/
  /*События*/
  .events-filters {box-shadow: none !important;}
  .events-list, .events-list-item {border: none !important;}
  .events-list td, .events-list th {box-shadow: none !important;}
  /********************/
  /*Музыка*/
  .globalchart .globalchart-item:first-child {background: none !important;}
  .globalchart {z-index: auto !important;}
  .two-column-layout .content-top, .two-column-layout .content-top .container, .two-column-layout .container {background: none !important;}
  .container {background: none !important;}
  .globalchart .globalchart-item:first-child {background: none !important;}
  /********************/
  /*Библиотека*/
  div.graph-container {display: none !important;}
  .scrobbles-layout .fluid-container {background: none !important;}
  .scrobbles-layout .overlay-control-bar, .scrobbles-layout .overlay-content {border: none !important;}
  .scrobbles-layout .overlay-side-rail {background: none !important;}
  .scrobbles-layout .scrobbles-nav-bar .secondary-nav {background: none !important;}
  .scrobbles-layout .scrobbles-nav-bar .secondary-nav-item-link.secondary-nav-item-link--active {background: black !important;}
  .scrobbles-layout .overlay-control-bar::after {border: none !important;}
  /**/
  /*Подвал*/
  .footer-wrapper {background: none !important;}
  .footer-wrapper .container .footer .site-links {box-shadow: none !important;}
  /********************/
  /*Реклама*/
  .related-ads {display: none !important;}
  /********************/
  .yad-lightbox, .yad-lightbox-content {background: none !important;}
  /*******************/
  .main-content::before {background: none !important;}
  /*[[youtube]]*/
}
@-moz-document domain("yimg.com") {
  .yad-widget li {border: none !important;}
  .yad-widget .yad-title.C {color: white !important;}
  .yad-widget .yad-sponsored {background: none !important;}
  /********************/
}

