/* @MahmoodFeiz */
/* Created on Aug 9, 2015 */
/* Updated on Nov 14, 2015 */
/* Optimized for IranSans v4.0 */
/* IranSans font (which is NOT a free font) is created and licensed by Moslem Ebrahimi (fontiran.com) */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('https://twitter.com/') {

  .tweet-text {
    font-family: IRANSans;
    font-weight: 300;
    line-height: 22px;
  }

  .tweet-text-rtl {
    font-family: IRANSans;
    font-weight: 300;
    font-size: 1em;
    line-height: 22px;
  }

  .tweet-box {
    font-family: IRANSans;
    font-weight: 300;
  }
  
  .TwitterCard-container {
    font-family: IRANSans;
    font-weight: 300;
  }
  
  .fullname {
    direction: rtl;
    font-family: IRANSans;
    font-weight: bold;
  }
  
  .bio, .account-bio, .prf-bio, .profile-card, .ProfileCard {
    font-family: IRANSans;
    font-weight: 300;
  }
}

@-moz-document url-prefix('https://tweetdeck.twitter.com/') {
  
  .is-wide-columns .column {
    width: 428px;
  }
  
  input[type='text'], textarea {
    direction: rtl;
    font-family: IRANSans;
    font-weight: 300;
  }

  .tweet-text, .js-tweet-text {
    direction: rtl;
    font-family: IRANSans;
    font-weight: 300;
    text-align: right;
    line-height: 22px;
  }

  .tweet-text-rtl {
    font-family: IRANSans;
    font-weight: 300;
    font-size: 1em;
    line-height: 22px;
  }

  .tweet-box {
    font-family: IRANSans;
    font-weight: 300;
  }
  
  .TwitterCard-container {
    font-family: IRANSans;
    font-weight: 300;
  }
  
  .account-bio, .prf-bio {
    direction: rtl;
    font-family: IRANSans;
    font-weight: 300;
  }
  
  .fullname {
    direction: rtl;
    font-family: IRANSans;
    font-weight: bold;
  }
  
  .account-link {
    font-family: IRANSans;
    font-weight: 300;
  }
  
}
