style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
.ac-type-icon,
#feed-button,
.search-go-button,
.search-go-button-stack,
#go-button,
.search-go-button {
    display: none !important
}
#TabsToolbar {
    background: rgba(23, 23, 23, 100) !important;
}
#main-window:not([privatebrowsingmode="true"])[sizemode="normal"] #TabsToolbar {
    margin-left: 1px !important;
    margin-right: 0px !important;
    margin-top: 0px !important;
}
#main-window:not([privatebrowsingmode="true"])[sizemode="maximized"] #TabsToolbar {
    margin-right: 0px !important;
    margin-left: 0px !important;
    margin-top: 0px !important;
}
#main-window:not([privatebrowsingmode="true"])[sizemode="fullscreen"] #TabsToolbar {
    margin-right: -53px !important;
    margin-left: 0px !important;
    margin-top: 0px !important;
}
#TabsToolbar .arrowscrollbox-scrollbox {
    padding: 0px !important;
}
#TabsToolbar .tabbrowser-tabs {
    min-height: 32px !important;
    margin-bottom: 0px !important;
}
#TabsToolbar .tabbrowser-tab {
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    border-color: rgba(43, 43, 43, .20) !important;
    border-width: 0px 1px 0px 1px !important;
    padding: 0px 0px !important;
    background: transparent !important;
    background-clip: padding-box !important;
    transition: all .4s !important;
    margin-left: -1px !important;
}
#TabsToolbar .tabs-newtab-button {
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    border-style: solid !important;
    border-color: transparent !important;
    background: rgba(255, 255, 255, .0) !important;
    background-clip: padding-box !important;
    transition: all .11s !important;
    opacity: .3 !important;
}
.scrollbutton-up[disabled="true"] {
    border-style: solid !important;
    border-right-width: 1px !important;
    border-right-color: rgba(218, 218, 218, 100) !important;
    margin-left: -1px !important;
    margin-right: -1px !important;
}
#TabsToolbar tab:not([selected="true"]) {
    color: rgba(255, 255, 255, .7) !important;
}
#TabsToolbar .tabbrowser-tab[selected] {
    color: rgba(255, 255, 255, 1.0) !important;
    background: rgba(43, 43, 43, 100) !important;
    background-clip: padding-box !important;
    border-color: rgba(43, 43, 43, 100) !important;
    border-top-color: transparent !important;
}
#TabsToolbar .tabs-newtab-button:hover:not([selected]) {
    border-color: rgba(130, 130, 130, .0) !important;
}
#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
    border-color: rgba(45, 45, 45, 100) !important;
    border-top-color: rgba(45, 45, 45, 100) !important;
    background-color: rgba(45, 45, 45, 100) !important;
    color: rgba(255, 255, 255, 1.0) !important;
}
#TabsToolbar .tab-background {
    margin: 0 !important;
    background: transparent !important;
}
#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display: none !important;
}
#TabsToolbar .tab-background-middle {
    margin: -4px -2px !important;
    background: transparent !important;
}
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}
#TabsToolbar .tabs-newtab-button {
    border-width: 0px !important;
    margin: 0px !important;
    width: auto !important;
    padding: 0px 6px !important;
}
#addon-bar,
#PersonalToolbar,
#main-window:not([privatebrowsingmode])[sizemode="maximized"] #nav-bar {
    border-top-color: transparent !important;
    padding-right: 5px !important;
}
#navigator-toolbox::after {
    background: rgba(43, 43, 43, 100) !important;
}
#nav-bar {
    background: rgba(43, 43, 43, 100) !important;
    box-shadow: none !important;
    background-clip: padding-box !important;
    border-color: transparent !important;
    border-bottom-right-radius: 0px !important;
}
#PanelUI-button {
    background-image: none !important;
}
#nav-bar-customization-target {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}
#nav-bar .toolbarbutton-1:not([type="menu-button"]),
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-button,
#nav-bar .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker {
    padding-top: 5px !important;
    padding-bottom: 6px !important;
}
#back-button > .toolbarbutton-icon {
    margin-top: -1px !important;
    margin-bottom: -1px !important;
}
#forward-button > .toolbarbutton-icon {
    margin-top: -1px !important;
    margin-bottom: 1px !important;
    border-radius: 5px !important;
    border-color: transparent !important;
    margin-left: 5px !important;
}
#nav-bar {
    margin-top: 0px !important;
    margin-left: 0px !important;
    margin-right: 0px !important;
    margin-bottom: 0px !important;
    padding: 0px !important;
    border-radius: 0px !important;
    border-top: 0px #282B34!important;
    box-shadow: none !important;
}
#main-window:not([privatebrowsingmode])[sizemode="normal"] #nav-bar {
    background: rgba(43, 43, 43, 100) !important;
    margin-top: 0px !important;
    margin-left: 1px !important;
    margin-right: 1px !important;
    margin-bottom: 0px !important;
    padding: 0px !important;
    border-radius: 0px !important;
    border-top: 0px #282B34!important;
    box-shadow: none !important;
}
#urlbar {
    background: transparent !important;
    padding: 8px !important;
    text-align: left!important;
    padding-left: 8px !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    margin-left: 6px !important;
    margin-right: 4px !important;
    border: 0px solid transparent!important;
    color: #555555 !important;
    font-family: "Segoe UI Semibold", Helvetica, Arial, sans-serif;
    font-size: 14px;
    box-shadow: none !important;
}
#searchbar .search-go-button {
    visibility: hidden !important;
}
#searchbar .searchbar-dropmarker-image {
    display: none !important;
}
#searchbar .searchbar-engine-image {
    width: 0 !important;
    padding-left: 18px !important;
    margin-left: -20px !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    margin-right: 2px !important;
}
#searchbar .searchbar-textbox {
    background: transparent !important;
    text-align: left!important;
    margin-top: 7px !important;
    margin-bottom: 7px !important;
    margin-left: 1px !important;
    margin-right: 10px !important;
    border-top: 1px solid transparent!important;
    border-bottom: 1px solid transparent!important;
    border-left: 1px solid transparent!important;
    border-right: 1px solid transparent!important;
    padding-left: 5px !important;
    color: #757983 !important;
    font-family: "Segoe UI Semibold", Helvetica, Arial, sans-serif;
    font-size: 12px;
    box-shadow: none !important;
}
#urlbar,
.searchbar-textbox {
    border-radius: 3px !important;
}
.urlbar-input {
    padding-left: 10px !important;
}
#urlbar #plugins-notification-icon,
#urlbar #notification-popup-box {
    display: none!important;
}

/*===============================================
  SINGLE TAB
===============================================*/

.tab-close-button {
    -moz-appearance: none!important;
    pointer-events: auto!important;
    list-style-image: url("data:image/png;base64,")!important;
    -moz-image-region: rect(0, 14px, 14px, 0)!important;
}
.tab-close-button * {
    width: 12px !important;
    height: 12px !important;
}
.tab-close-button:hover,
.tab-close-button:hover[selected="true"] {
    -moz-image-region: rect(0, 14px, 14px, 0)!important;
}
.tab-close-button:hover:active,
.tab-close-button:hover:active[selected="true"] {
    -moz-image-region: rect(0, 36px, 14px, 24px)!important;
}
.tabbrowser-tab:not([pinned]) .tab-icon-image {
    margin-left: 0px !important;
    margin-right: 0px !important;
    /*favicon*/    
    display: block !important;
    margin: 0px 10px 0px 0px!important;
    /*filter: grayscale(40%);*/
}
.tabbrowser-tab[pinned] .tab-text {
    margin-left: 0px !important;
    margin-right: 0px !important;
    /*favicon*/    
    display: none !important;
}
.tabbrowser-tab[pinned] .tab-icon-image {
    margin-left: 4px !important;
    margin-right: 0px !important;
    /*favicon*/    
    display: block !important;
}
.tabbrowser-tab:not([pinned]) .tab-text {
    margin-left: 0px !important;
    margin-right: 0px !important;
}
tab .tab-close-button {
    display: none !important;
}
tab:hover .tab-close-button {
    display: -moz-box !important;
}

tab:hover .tab-close-button[pinned]{
    display: none !important;
}

