style.css:
@-moz-document domain("gamevn.com") {
body:not([contenteditable="true"]){
    background: url(http://pik.vn/2015eb3aad18-6560-4ba9-aa9b-c88d0e993527.jpeg) fixed !important;
    background-size: 100% !important;
}

#headerProxy, #header, .discussionList.sectionMain, div.listBlock.stats.pairsJustified, .discussionListItem, .sectionMain.pollBlock.AutoValidator.PollVoteForm, form>.secondaryContent, #forums > li > div > div.nodeText, #forums > li, .baseHtml.signature.messageText.ugc, .message .messageInfo {
	background: transparent !important;
}
#content .pageContent, .discussionListItem.visible.unread, [id*="post-"].message, #forums {
  background-color: rgba(255, 255, 255, 0.4) !important;
}

aside>.attribution.type, aside>.quoteContainer{
	background: rgba(225, 228, 242, 0.5) !important;
}
.messageList .messageUserInfo{
	    margin-right: 0px !important;
}
.message .messageContent{
	background-color: rgba(208, 219, 231, 0.4) !important;
	padding-left: 15px !important;
}

li[data-author="DAC"] .SpoilerTarget.bbCodeSpoilerText{
	display: block;
}
}
