style.css:
@-moz-document domain("www.poemods.com") {
html, body {
	background-color: #000;
}
td {
	background-color: #000 !important;
    color: #bbb;
}
li {
	color: #bbb;
}
a, a:visited {
	color: #8787A0;
}
#mod-results table {
	background-color: rgba(53, 50, 50, 0.39);
}
#show-query > h3 {
    color: #bbb;
}
#mod-results > table {
    background-color: transparent !important;
}
}
