/* |-----------------------| */ 
/* | Einbau in WhatsAppWeb | */
/* |-----------------------| */ 
@-moz-document url-prefix("https://web.whatsapp.com/") {


/* |----------------| */ 
/* | Fenster Breite | */
/* |----------------| */ 
.app {
  width: 100%; 
  height:100%;
  top: 0px;}



/* |---------------------| */ 
/* | Chat Fenster Breite | */ 
/* |---------------------| */ 
.pane-chat-body {
  width:100%; 
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}
  
.pane-chat {
  width:100%; 
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}


/* |---------------------------| */ 
/* | Startseiten (Chat) Breite | */  
/* |---------------------------| */ 
.pane-intro {
  width:100%; 
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}


/* |--------------------| */ 
/* | Startseiten Banner | */ 
/* |--------------------| */ 
.intro-image {
  width:450px; 
  height:300px; 
  background-image: url(http://www.Mystic-City.de/Designs/WhatsApp/Banner.gif);
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);}
  
.pane-intro:after {
  display:none;}

  
/* |----------------| */ 
/* | Dropdown Menus | */   
/* |----------------| */ 
.dropdown {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}

.dropdown a:hover{
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg); 
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);
  border-bottom: 1px solid #ff0000}


/* |-----------| */ 
/* | User List | */ 
/* |-----------| */ 
.chat {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}

.chatlist .active.location,.chatlist .chat.active {
  border-bottom:1px solid #FF0000}

.chat:hover {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg); 
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);}

.chatlist .chat:hover{
  border-bottom: 1px solid #ff0000}

.chat{
  border-bottom:1px solid #000000}

.chatlist .chat:after {
  border-bottom:0px solid #000000}

.chat-status {
  font-size:90%;
  color:#000000;
  font-weight:bold;}
  
.chat-title,.header-title ,.location-main, .dropdown .menu-item a {
  color:#000000;
  font-size:100%;
  font-family:'Arial';
  font-weight:bold;}


/* |---------------| */ 
/* | Chat Messages | */ 
/* |---------------| */ 
.message-in {
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);
  border:0px ridge rgba(0,0,0,0.5);
  border-radius: 10px;
  color:#000000;}

.message-out,.msg-unread-body{
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);
  border:0px ridge rgba(0,0,0,0.5);
  border-radius: 10px;
  color:#000000;}

.message-datetime {
  color:#FF0000;}

.message-in:before,.message-out:before{
  display:none;}
  
.block-compose .input-container{
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);
  border:2px ridge rgba(0,0,0,0.5)}

.message-system-body {
  box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3);
  border:0px ridge rgba(0,0,0,0.5);
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);
  color:#0000FF;}


/* |-----------| */ 
/* | User Info | */ 
/* |-----------| */ 
.drawer {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg)}

.well {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png)}

.app-wrapper::before {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg)}
  
body {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg)}
  
.drawer-profile .drawer-header {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg)}
  
.drawer-header {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg)}


/* |-------| */ 
/* | Suche | */ 
/* |-------| */ 
.cont-input-search {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);
  border:0px ridge rgba(0,0,0,0.5);}

.input-search {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/ChatBack.png);}

.subheader-search {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}

  
/* |--------------| */ 
/* | Akku Anzeige | */ 
/* |--------------| */ 
.butterbar {
  background-image: linear-gradient(to bottom,#000000,#ff0000);}


/* |--------| */ 
/* | Header | */ 
/* |--------| */ 
.pane-header {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);}

  
/* |-------------| */ 
/* | Smiley Menu | */ 
/* |-------------| */ 
.menu-tabs-emoji {
  background-image:url(http://www.Mystic-City.de/Designs/WhatsApp/Background.jpg);
  border:5px ridge rgba(0,0,255,0.5);}

.emoji-panel {
  border:5px ridge rgba(0,0,255,0.5);}
  
.icon-smiley, .icon-hide, .block-compose .icon-ptt,  .block-compose .icon-send, .icon-menu, .icon-chat, .icon-clip, .menu-horizontal>.menu-item,.menu-horizontal>span>.menu-item {
  background-color:#ffcc00;
  border-radius:55px;
  padding:6px}

.icon-smiley:hover, .icon-hide:hover, .block-compose .icon-ptt:hover,  .block-compose .icon-send:hover, .icon-menu:hover, .icon-chat:hover, .icon-clip:hover, .menu-horizontal>.menu-item:hover,.menu-horizontal>span>.menu-item:hover {
  background-color:#ffff00;
  border-radius:55px;
  padding:6px}


/* |------| */ 
/* | Ende | */ 
/* |------| */ 

}
