style.css:
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #333;
}

::-webkit-scrollbar-thumb {
    background: #888888;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #333;
}
