style.css:
@-moz-document url-prefix('https://hummingbird.me/dashboard') {
/*Remove Trending Groups, Industry News, Community Discussions*/
div.community-widget-panel
    {	
      	display: none
	}

/*Move sidebar to the left*/
div.secondary-info
	{
		left: 30%
	}

/*Increase feed size - font size, posts & updates*/
div.user-feed
	{
    	width: 70%; left: 15%; font-size: 23px;
    }
div.user-feed-filter
	{
    	font-size: 23px;
    }
}
