@-moz-document regexp(".*kinopoisk.ru/(article|news|interview|photostory|podcast|media)/.*") {
.media-post-card__image-wrapper,.media-post-card__image{border-radius:4px 4px 4px 4px!important}
.media-post-card__meta{position:absolute}
.media-post-card__header{margin-top:30px}
.media-post-card__main{background:#fff}
.media-post-page>div{display:none!important}
.media-post-page>.media-article,.media-post-page>.media-news,.media-post-page>.media-page-layout{display:block!important}
.media-post-card__main{padding:20px 0!important}
}

