style.css:
@-moz-document domain("sina.com.cn") {
  /*---------------------------*/
  /*         广告过滤           */
  /*---------------------------*/
  /*评论广告*/div#SI_Comment_DA,
  /*两侧浮动*/div.sinaad-toolkit-box,
  /*画中画*/div[id^="ad_"],div[class^="Ad_"], 
  div.adNone, ins.sinaads
  {display:none!Important;}
}

@-moz-document regexp("http://[\\w\\.]+\\.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"), regexp("http://slide.news.sina.com.cn/\w+/slide_.*"){
  /*---------------------------*/
  /*   新闻文章页 拉宽规则 版本2   */
  /*---------------------------*/
  /* http://news.sina.com.cn/c/gat/2017-12-25/doc-ifypwzxq6494881.shtml */
  /* http://sports.sina.com.cn/g/pl/2017-12-25/doc-ifypxrpp3747350.shtml */

  /*右栏隐藏*/
  div.article-content-right{display:none!Important;}

  /*文章页主体框架拉宽*/
  div.article-content-left{width:auto!important;margin:0px!important;}

  /*文章页主体框架美化*/
  div#article {background:#F5F8FD !important;border:1px #C8D8F2 solid !important;padding: 5px 20px!important;letter-spacing:1px!important;}

  /*新浪体育 微信公众号*/
  div.blk-wxfollow,

  /*相关新闻 & 相关微博*/
  div#tab_related
  {display:none!Important;}

  /* -----===== 文章页广告 =====-----*/
  /*文章上方bar*/
  div#top_bar_wrap,
  /*新闻顶部Banner*/
  div.main-content>div.top-banner
  {display:none!Important;}
}

@-moz-document regexp("http://[\\w\\.]+\\.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"), regexp("http://slide.news.sina.com.cn/\w+/slide_.*"){

  /*---------------------------*/
  /*        新闻文章页          */
  /*---------------------------*/
  /*主体拉宽*/
  div#articleContent>div.left{width:auto!Important;}
  /*文章美化*/
  div#artibody {background:#F5F8FD !important;border:1px #C8D8F2 solid !important;padding: 5px 20px!important;letter-spacing:1px!important;}
  /*右栏隐藏*/
  div#articleContent>div.right{display:none!important;}
  /*评论拉宽*/
  div.Mblk_cmnt, div.t_content, div#J_Comment_Form_B{width:auto!important;}
  textarea.J_Comment_Content{width:895px!important;}
  /*评论内容拉宽*/
  div.comment_content>div.t_txt{width:auto!Important;}
  /*评论浮动在最上面*/
  div#J_Comment_Form_B{top:0px!important;}
  /*文章下侧工具条*/
  div#bottomTools{display:none!important;}
  /*文章右侧工具条*/
  div#desktopSideTools{display:none!important;}
  /*微博推荐*/
  div#wb_rec{display:none!important;}
  /*相关阅读*/
  div#relatedNewsWrap{display:none!important;}
  /*新闻心情*/
  div#moodcounter{display:none!important;}
  /*新浪微信公众号*/
  div.news_weixin_ercode{display:none!important;}
  /* -----===== 文章页广告 =====-----*/
  /*评论上方通栏广告*/
  /*娱乐频道*/ div.ad_content, div.content_left_topAd, div.left-module.ad

  {display:none!important;}
}

@-moz-document regexp("http://ent.sina.com.cn/[\\w/]+/[\\d-]{8,10}"){
  /*---------------------------*/
  /*        娱乐版面　          */
  /*---------------------------*/
  /*文章版面拉宽*/
  div.content_wrappr_left {width:auto!important;}
  /*文章右栏隐藏*/
  div.content_wrappr_right {display:none!important;}
  /*聚焦AD*/
  div#left_focus_ad {display:none!important;}
}

@-moz-document regexp("http://mil.news.sina.com.cn/(\\w+/)?[\\d-]{8,10}"){
  /*---------------------------*/
  /*        新浪军事　          */
  /*---------------------------*/

  /* ===== 0.0.2 [2016.03.16] ===== */
  /*主体框架拉宽*/
  div.content_wrappr_left{width:auto!Important;}
  /*文章内容拉宽*/
  div#artibody, div.mbArticleSharePic{width:auto!Important;}
  /*右栏隐藏*/
  div#pl_side{display:none!Important;}
  /*广告隐藏*/
  div.ad, div#ad_top, div#pl_feedAd, div#left_hzh_ad, div#left_focus_ad{display:none!Important;}

  /*主体拉宽*/
  div.blkContainer{width:100%!important;}
  div.blkContainerSblk{width:auto!Important;}
  /*文章图集*/
  div.hdFigureWrap, div.figureTableDiv, div.titleToolBar{width:auto!important;}
  /*文章图集介绍*/
  td.rightTable{width:auto!Important;}
  /*文章图集预览图滚动*/
  div.thumbDisplay{width:819px!important;}
  div.scrollDiv{width:100%!Important;}
  div.scrollBar{width:500px!important;}
  /*评论拉宽*/
  div.Mblk_cmnt, div.t_content{width:auto!Important;}
  /*右栏隐藏*/
  div.sidebar{display:none!important;}
}

@-moz-document regexp("http://hb.sina.com.cn/[\\w/]+/[\\d-]{8,10}/(?:detail-\\w+|\\d+)"){
  /*---------------------------*/
  /*        湖北地方站          */
  /*---------------------------*/
  div.con{width:auto!Important;}
  /*右栏隐藏*/div.side, /*文章画中画*/div.article-video, /*底部广告*/div.article-ad{display:none!important;}
  /*文章美化*/
  div#artibody {background:#F5F8FD !important;border:1px #C8D8F2 solid !important;padding: 5px 20px!important;letter-spacing:1px!important;}
}

@-moz-document regexp("http://sports.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"){

  /*---------------------------*/
  /*    新浪体育 文章页拉宽规则    */
  /*---------------------------*/

  /*右栏隐藏*/
  div#j_side{display:none!important;}

  /*文章页主体框架拉宽*/
  div.layout-wrap-b{width:auto!important;}

  /*相关阅读隐藏*/
  div#j_wrap_4{display:none!important;}

  /*右侧浮动——新浪新闻体育客户端*/
  div.side-btns-2wm{display:none!important;}
}


@-moz-document regexp("http://finance.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"){

  /*---------------------------*/
  /*    新浪财经 文章页拉宽规则    */
  /*---------------------------*/
  /*左右两侧浮动*/
  div[class^="side-ad-"],

  /*正文下方 微信二维码*/	div.fin_art_weixin,
  /*正文下方 理财平台*/ 	div#lcs_wrap,
  /*正文下方 理财师推荐*/	div#lcs1_w,
  /*<!-- 正文页下四张图片通栏 begin-->*/
  iframe[src$="?from=new_content_4pic"]
  {display:none!important;}
}

@-moz-document regexp("http://auto.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"), url-prefix("http://auto.sina.com.cn/j_kandian.d.html"){

  /*---------------------------*/
  /*    新浪汽车 文章页拉宽规则    */
  /*---------------------------*/

  /*正文框架*/ div.con-left.fL{width:auto!Important;}
  /*正文右栏*/ div.con-right.fR, div#rightSinaFuyiArea{display:none!important;}

  /*正文下方 分享按钮*/	div[data-sudaclick="auto_zwy_kuailefenxiang"],
  /*正文下方 微信二维码*/	div.sina-code,
  /*右下角 询价平台*/div#bottomInquryBar
  {display:none!important;}
}

@-moz-document regexp("http://tech.sina.com.cn/([\\w/]+/)?[\\d-]{8,10}/.*"){

  /*---------------------------*/
  /*    新浪科技 文章页拉宽规则    */
  /*---------------------------*/

  /*正文内容 主体*/ div.content_wrappr_left{width:auto!Important;}
  /*正文右栏 隐藏*/ div#pl_side{display:none!important;}

  /*正文下方 聚焦*/ div#left_focus_ad,
  /*新浪广告*/ div.sinaAd
  {display:none!important;}
}
