style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
 #customization-palette > toolbarpaletteitem > label {font-family: constantia !important}
#customization-palette > toolbarpaletteitem {color:#3ce !important;font-family:  constantia !important;text-shadow: 1px 2px 1px rgba(0,0,0,.3) !important;box-shadow: 0px 0px 9px rgba(0, 0, 0, .8 )inset!important; border: 1px solid #555 !important; border-radius: 15px 0px !important}
#customization-palette>toolbarpaletteitem:hover{background-color: rgba(0,0,0,.75) !important; border: 1px solid #555 !important;transition:all 750ms ease-in-out !important;color:#3ce!important}
#customization-palette > toolbarpaletteitem:active {background-color: rgba(0,0,0,.75) !important; border: 1px solid #000 !important;color:#3ce!important}
#customization-palette {background: linear-gradient(to bottom  , rgba(50, 50, 50, 0.1) 1%, rgba(0, 0, 0,  0.95) 65%)!important;    box-shadow: 0px 0px 10px rgba(0, 0, 0, .9 )inset!important;border: 1px solid #000 !important;border-radius: 15px 15px 0px 0px !important}		
#customization-header {background: transparent !important;text-align:center!important;color:#3ce!important}
#customization-panel-container,#customization-container {background: linear-gradient(to bottom  , rgba(50, 50, 50, 0.95) 3%, rgba(0, 0, 0,  0.95) 40%)!important;}
#customization-panel-container .panel-arrowcontent,#PanelUI-popup > .panel-arrowcontainer > .panel-arrowcontent { 
    color:#3ce !important;background: linear-gradient(to bottom  , rgba(50, 50, 50, 0.8) 10%, rgba(0, 0, 0,  0.9) 60%)!important; box-shadow: 0px 0px 13px rgba(0, 0, 0, .8 )!important;border: 2px solid #000 !important; border-radius: 9px !important; }
#cut-button, #copy-button, #paste-button { color:#3ce !important}
.customizationmode-button {color: #3ce !important}	
.customizationmode-button:not(:hover) {border: 1px solid #222 !important}
.customizationmode-button:hover {border: 1px solid #3ce !important}
#customization-footer {background: linear-gradient(to bottom  , rgba(0, 191, 255, 0.01) 23%, rgba(0, 0, 0,  0.45) 100%)!important;box-shadow: 0px 0px 13px rgba(0, 0, 0, .8 )!important;    border: 1px solid #000 !important}
