style.css:
@-moz-document domain("outlook.office.com"), domain("outlook.office365.com") {
/*------------------------*/
/* font size */
.ms-font-xxl{
    font-size: 26px;
}
.conductorContent .ms-font-l{
    font-size: 14px;
}
.conductorContent .ms-font-m{
    font-size: 13px;
}
.conductorContent .ms-font-s{
    font-size: 11px;
}

/*------------------------*/
/* header */
/*
.ms-icon-modifier-doughboy.ms-icon-font-size-50, .ms-icon-modifier-doughboy.ms-icon-font-size-50:before {
    height: 40px;
    width: 40px;
}
.ms-icon-font-size-50 {
    font-size: 40px!important;
}
._ho2_6 {
    width: auto;
    margin: 0 10px 0 6px;
}
*/

/*------------------------*/
/* folder list */

/*------------------------*/
/* container */
._n_v1 _n_b1 div:last-child{ 
    top: 42px !important;
}
/*------------------------*/
/* Context Menu */
.contextMenuDropShadow {
    box-shadow: 0 0 1px rgba(0,0,0,0.2);
    border: 1px solid #d8d8d8;
}

/* "today", "yesterday", and day container */
._lvv_e2 {
    line-height: 20px;
    border-bottom: 1px solid #ddd;
}

/*------------------------*/
/* Message List View */
/*------------------------*/
/* mail container */
._lvv_H1 {
    top: 0px;
    height: 39px;
    border-bottom: 1px solid #e0e0e0;
}
/* unread marker */
._lvv_M1 {
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 38px;
}
/* checkbox */
._lvv_N1 {
	top: 1px;
    padding-top: 0px;
}
/* mail title area */
._lvv_P1 {
    height: 17px;
    padding: 3px 0 0 0;
}
/* mail title text */
._lvv_R1 ._lvv_Q1 {
    margin-top: 1px;
    height: 17px;
}

/* mail container with preview text */
._lvv_H1._lvv_I1 {
    height: 59px;
    border-bottom: 1px solid #e0e0e0;
}
/* unread marker with preview text */
._lvv_I1 ._lvv_K1 {
    height: 58px;
}
/* preview text conteiner */
._lvv_K1 ._lvv_Q1{
	height: 18px;
}
._lvv_Q1 {
	margin-top: 1px;
}

/*------------------------*/
/* Thread View */
/*------------------------*/
/* mail container */
._lvv_w {
    top: 0px;
    height: 39px;
    border-bottom: 1px solid #ddd;
}
/* unread marker */
._lvv_C {
    height: 39px;
}
/* checkbox */
._lvv_D {
	top: 1px;
    padding-top: 0px;
}
/* mail title area */
._lvv_I {
    height: 17px;
    padding: 3px 0 0 0;
}

/* mail container with preview text */
._lvv_w._lvv_x {
    height: 59px;
    border-bottom: 1px solid #ddd;
}
/* unread marker with preview text */
._lvv_x ._lvv_C { 
    height: 59px;
}

/* history */
._elv_l { /* container*/
	height: 23px;
}
._elv_n { /* history icon (history icon is background-image)*/
	height: 23px;
    margin-top: -4px
}
._elv_w { /* checkbox */
	top: 1px;
}
._elv_r { /* return icon */
	top: 0px;
}
._elv_L { /* sender */
	margin: 3px 0 0 0;
}
._elv_H, ._elv_I { /* folder */
	top: 4px;
}
}
