style.css:
@-moz-document domain("my.secondlife.com") {
@-moz-keyframes glow {
	0% {
		border: 1px solid #FFFFFF;
		-webkit-box-shadow: 0 0 4px #FFFFFF;
		box-shadow: 0 0 4px #FFFFFF;
		-webkit-opacity: 0.0;
		-khtml-opacity: 0.0;
		-o-opacity: 0.0;
		opacity: 0.0;
	}

	100% {
		border: 1px solid #FACDB0;
		-webkit-box-shadow: 0 0 8px #FACDB0;
		box-shadow: 0 0 8px #FACDB0;
		-webkit-opacity: 1.0;
		-khtml-opacity: 1.0;
		-o-opacity: 1.0;
		opacity: 1.0;
	}
}

/* < - - BEGINNING OF THE OVERALL MODIFICATIONS - - > */
/*CUSTOM CURSORS*/
html {
	cursor: url("http://i.imgur.com/zU5j1pD.png"), progress !important;
}

input,
textarea {
	cursor: url("http://i.imgur.com/2DFykf2.png"), progress;
}

a,
.tag,
select,
input.radio,
button.button,
input.checkbox,
input.checkbox,
input[type="file"],
input[type="radio"],
input[type="checkbox"],
input.button[type="submit"],
#stay_logged_in {
	cursor: url("http://i.imgur.com/2SqWuU3.png"), progress !important;
}

.section_loader,
.ajax_loader {
	cursor: url("http://i.imgur.com/7lzEFrh.png"), progress !important;
}

@keyframes fade-in {
	0% {
		opacity: 1
	}
	50% {
		opacity: 0;
		height: 16px;
	}
	100% {
		height: 0;
		opacity: 0;
	}
}

#statusbar .status_content {
	position: fixed !important;
	z-index: 999999 !important;
	text-align: center;
	width: 976px;
	animation: 3s fade-in 5s linear alternate 1 forwards;
	height: 16px;
}

/*General background*/
body#web {
	background: url('https://c2.staticflickr.com/9/8647/30219334841_014d10476f_o.jpg') 100% 100% !important;
	background-repeat: no-repeat !important;
	background-attachment: fixed !important;
	background-position: 25% !important;
	-webkit-background-size: cover !important;
	-o-background-size: cover !important;
	background-size: cover !important;
	overflow-y: auto !important;
	overflow-x: hidden !important;
}

#header {
	background: url("http://i.imgur.com/kgyLeGF.png") repeat-x;
	position: fixed !important;
	width: 996px !important;
	z-index: +99999 !important;
	top: 0px;
	height: 40px !important;
}

#header a {
	color: #bbb !important;
	font-weight: normal;
}

#header a:hover {
	color: #fff !important;
}

.icon-search_arrow,
#search_form span.search_button i {
	background: url("http://i.imgur.com/TVrYoAF.png");
	background-position: -61px -198px;
	background-repeat: no-repeat;
	width: 23px;
	height: 23px;
	margin-top: -3px;
}

#header #search_form #q {
	background: #444;
	transition: width 0.4s ease-out 0s;
	border: 1px solid #111;
}

#header #search_form #q:hover {
	transition: width 0.3s linear 0s !important;
	width: 300px;
}

body#web.signed_in div#inner {
	top: -20px;
}

body#web #wrapper.no_sidebar #index_header {
	margin-top: 40px !important;
}

#pick_image_zoom.simple_overlay,
#fl_image_zoom.simple_overlay,
#sl_image_zoom.simple_overlay,
#group_image_zoom.simple_overlay {
	background: #161616;
	border-color: #333;
}

#fl_image_zoom.simple_overlay img,
#sl_image_zoom.simple_overlay img,
#pick_image_zoom.simple_overlay img,
#group_image_zoom.simple_overlay img {
	border: 1px solid #333;
	width: auto;
}

/*General wrapper content*/
#wrapper {
	padding-left: 0px;
	padding-right: 0px;
	background-clip: padding-box !important;
	border-left: 1px solid rgba(0, 0, 0, 0);
	border-right: 1px solid rgba(0, 0, 0, 0);
	box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8);
}

#main,
.feed * {
	color: #999 !important;
}

#web #wrapper.no_sidebar #inner #main {
	background: #222;
	width: 996px;
}

/*Signed-in wrapper content*/
body#web #wrapper,
#web.signed_in #wrapper {
	background: url("http://i.imgur.com/bnEO5of.png") repeat-y;
	padding-top: 40px;
}

/*Re-positioned due to the sidebar being set in fixed position*/
#web.signed_in #wrapper #inner #main {
	margin-left: 210px;
}

/*That is some fake borders...*/
.ui-tabs .ui-tabs-nav,
#main .tabs,
#main .tabs ul li.selected {
	background: none;
}

/*Active tab*/
.ui-state-active a,
#main .tabs ul li.selected {
	border-bottom: 4px solid #F30 !important;
	font-weight: 700;
	color: #FFF;
}

/*Active tab (Inbox)*/
#main .tabs ul li.selected a {
	font-weight: 700;
	color: #FFF;
}

.ui-tabs .ui-tabs-nav li a,
#main .tabs ul li a {
	border: transparent;
	text-shadow: none;
	color: #AAA;
	font-weight: normal;
	text-transform: uppercase;
}

.ui-tabs .ui-tabs-nav li a:hover {
	color: #EEE;
}

.feed a,
#about_me a,
#notifications_list a {
	color: #BBB !important;
}

.feed a:hover,
#about_me a:hover,
#notifications_list a:hover {
	color: #F30 !important;
}

.activities_filter ul li a {
	color: #AAA;
	text-decoration: none;
}

.activities_filter ul li a:hover {
	color: #EEE;
	text-decoration: none;
}

.activities_filter ul li a.active {
	color: #F30;
	text-decoration: none;
}

.ajax_loader {
	background: url("http://i.imgur.com/MaQV2KT.gif") !important;
	background-repeat: no-repeat !important;
	width: 152px !important;
	height: 12px !important;
	padding: 6px 152px 6px 0px !important;
}

.activity .activity_avatar,
.user_list .avatar {
	border: 3px solid #999;
	border-radius: 4px;
	margin-left: -4px;
}

.comment_avatar {
	border: 1px solid #999;
	margin-left: -1px;
}

#profile_settings img {
	border: 1px solid #999;
}

.ajax_loader {
	font-size: 0;
}

/*ADD-ON FIXED TABS*/
#main .tabs.tabs3,
.ui-tabs .ui-tabs-nav,
#main .tabs.tabs2 {
	background: #222;
	position: fixed;
	top: 40px;
	margin: 0 auto;
	float: none;
	z-index: 1;
	width: 786px;
}

.ui-tabs .ui-tabs-nav li a {
	border-bottom: 4px solid #222;
}

#web.signed_in #wrapper #inner #main {
	padding-top: 33.1px;
}
/*END OF ADD-ON FIXED TABS*/
/* < - - END OF THE OVERALL MODIFICATIONS - - > */
/* < - - BEGINNING OF THE RIGHT SIDE MODIFICATIONS - - > */
.profile_header,
.ui-tabs .ui-tabs-nav li a {
	background: #222;
}

#username {
	position: relative !important;
	top: -25px !important;
	color: #f30 !important;
	font-family: Arial !important;
	font-size: 24px !important;
	text-transform: capitalize;
	text-overflow: ellipsis;
}

#web.signed_in #wrapper #inner div#main .profile_header.clearfix .content .profile_actions.current_user a[href^="/settings/profile"] {
	color: #555;
	font-size: 11px;
	background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #fefefe 0px, #dedede 100%) repeat scroll 0 0;
	border: 1px solid #dedede;
	border-radius: 2px;
	box-shadow: none;
	padding: 2px 5px 2px 5px;
}

#web.signed_in #wrapper #inner div#main .profile_header.clearfix .content .profile_actions.current_user a[href^="/settings/profile"]:hover {
	background: #f1f1f1;
	text-decoration: none;
}

#web.signed_in #wrapper #inner div#main .profile_header.clearfix .content .profile_actions.current_user a[href^="/settings/profile"]:before {
	content: "";
	display: inline-block;
	background: url("http://i.imgur.com/TVrYoAF.png");
	background-position: -44px -204px;
	width: 15px;
	height: 13px;
	margin-right: 2px;
}

#main .tabs.tabs3,
.ui-tabs .ui-tabs-nav,
#main .tabs.tabs2 {
	border-bottom: 1px solid #575757;
}

/*Signed-in wrapper feed content */
#web.signed_in #wrapper #inner #main {
	width: 785px;
}

/*Share Button Red Temp*/
#posts_form .button.secondary {
	background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #f30 0px, #cc092f 50%, #6d0519 100%) repeat scroll 0 0;
	border: 1px solid #870000;
	border-radius: 3px;
	color: #fff;
}

/*Share Button Red Temp*/
#posts_form .button.secondary {
	color: #FFF !important;
}

.activities_filter ul {
	background: #161616;
	border: 1px solid #333;
	color: #999;
	font-size: 12px;
	margin: 0;
	padding: 3px;
	border-radius: 3px;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
}

.activities_filter ul:before {
	content: "";
	width: 610px;
	height: 1px;
	position: absolute;
	left: 10px;
	margin-top: 7px;
	border-bottom: 1px solid #666;
	display: block;
}

.section,
.activity {
	border: none;
}

.thumbnail {
	border: none;
}

.thumbnail_link {
	float: none !important;
}

.thumbnail_link img.thumbnail.small {
	display: none !important;
	width: 670px !important;
	cursor: default !important;
}

.thumbnail_link img.thumbnail.large {
	display: inline !important;
	cursor: default !important;
}

.feed a.icon_button {
	background: url("http://i.imgur.com/TVrYoAF.png") !important;
	background-position: -173px -487px !important;
	border: none !important;
	height: 17px !important;
	width: 19px !important;
	position: relative !important;
	right: 6px !important;
	top: -6px !important;
	z-index: 2 !important;
	display: block !important;
	padding: 0px !important;
	border-radius: 0 !important;
}

.feed a.icon_button:hover {
	background-position: -173px -511px !important;
}

.feed a.icon_button:active {
	background-position: -173px -535px !important;
}

.activity .menu_button .menu {
	background: #222;
	border-color: #333;
	top: 15px;
	right: 5px;
	z-index: 1;
	border-radius: 3px;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
}

/*Icons such as Like, Delete, Comment, Home, People, Inbox, Settings & Invite*/
.icon-love,
.icon-remove,
.icon-comment,
.icon_comment,
.icon-nav_home,
.icon-nav_inbox,
.icon_add_friend,
.icon-nav_people,
.icon-nav_settings,
.activity .remove_object,
.activity .remove_comment,
.activity .feedback_stats .love_stats,
.activity .feedback_stats .comment_stats,
#sidebar ul#web_navigation li a#home span,
#sidebar ul#web_navigation li a#inbox span,
#sidebar ul#web_navigation li a#people span,
#sidebar ul#web_navigation li a#settings span,
.feed_notice .feed_notice_actions .dismiss_notice {
	background-image: url("http://i.imgur.com/8fUw79P.png");
}


.menu_button .menu a {
	text-shadow: none;
	text-align: center;
}

.menu_button .menu a:hover {
	background: #333;
}

.activity_content,
.comment_content {
	background: #161616;
	border-radius: 7px;
	padding: 5px 15px 5px 15px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	transition: all 0.25s linear 0s;
}

.activity_content:hover,
.comment_content:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
}

.activity .activity_icon {
	margin-left: 30px;
	margin-right: 5px;
	margin-top: 9px;
}

/*Comments Reply*/
.feed .text_input_fullwidth {
	background: #2c2c2c !important;
	border: 3px solid #2c2c2c !important;
	border-radius: 5px;
}

/*Comments Reply*/
.feed .activity ul.comments li .text_input_fullwidth textarea {
	border: 1px solid transparent;
	background: #2c2c2c;
	height: 20px !important;
	overflow: hidden;
	transition: height 0.3s linear 0s !important;
}

/*Comments Reply*/
.feed .activity ul.comments li .text_input_fullwidth textarea:hover {
	border: 1px solid #666;
	background: #2c2c2c;
	height: 85px !important;
	overflow: hidden;
	transition: height 0.3s linear 0s !important;
}

.update_box .update_box_input_container * {
	color: #999 !important;
}

.update_box .update_box_input_container {
	background: #2c2c2c !important;
	border: 3px solid #2c2c2c !important;
	border-radius: 5px;
}

.update_box .update_box_input_container textarea {
	border: 1px solid transparent;
	background: #2c2c2c;
	height: 20px !important;
	overflow: hidden;
	transition: height 0.3s linear 0s !important;
}

.update_box .update_box_input_container textarea:hover {
	border: 1px solid #666;
	background: #2c2c2c;
	height: 135px !important;
	overflow: hidden;
	transition: height 0.3s linear 0s !important;
}

#about_me h3 {
	color: #CCC;
}

#parter,
#homepage,
#interests,
#birthdate,
#payment_info {
	border-bottom: 1px solid #333;
}

#sl_about_text,
#fl_about_text {
	background: #161616;
	border-radius: 3px;
	padding: 7px 7px 7px 7px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	margin: 15px;
}

#fl_image {
	padding-bottom: 0;
}

#about_tab .text_input_fullwidth {
	background: #2c2c2c !important;
	border: 3px solid #2c2c2c !important;
	border-radius: 7px !important;
	padding: 4px 7px 7px !important;
	width: 740px;
}

#about_tab .text_input_fullwidth * {
	color: #999 !important;
}

#notes textarea#note_text {
	height: 30px !important;
	overflow: hidden;
	transition: height 0.3s linear 0s !important;
}

#notes textarea#note_text:hover {
	height: 143px !important;
	border: 1px solid #666;
	overflow: auto;
	transition: height 0.3s linear 0s !important;
}

#interests.section a.tag {
	background: #161616;
	border-radius: 3px;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	text-shadow: none !important;
	color: #AAA !important;
	transition: all 0.25s linear 0s;
}

#interests.section a.tag:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
	color: #FFF !important;
}

#picks_list.listing.tight.hide li.item,
#groups_list.listing.tight.hide li.item {
	background: #161616;
	border-radius: 3px;
	padding: 5px 15px 5px 15px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	transition: all 0.25s linear 0s;
	margin: 10px;
}

#picks_list.listing.tight.hide li.item:hover,
#groups_list.listing.tight.hide li.item:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
}

#picks_list.listing.tight.hide li.item a,
#groups_list.listing.tight.hide li.item a {
	color: #AAA;
}

#picks_list.listing.tight.hide li.item a:hover,
#groups_list.listing.tight.hide li.item a:hover {
	color: #F30;
}

/*Picks*/
#web.signed_in #main .pick {
	background: #161616;
	border-radius: 3px;
	padding: 5px 15px 5px 15px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	transition: all 0.25s linear 0s;
	margin: 15px;
}

/*Picks*/
#web.signed_in #main .pick:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
}

/*Picks*/
#web.signed_in #main .pick a {
	color: #BBB;
}

/*Picks*/
#web.signed_in #main .pick a:hover {
	color: #F30;
}

/*Picks*/
#web.signed_in #main .pick h1 {
	color: #BBB;
}

/*Groups*/
#web.signed_in #main .group {
	background: #161616;
	border-radius: 3px;
	padding: 5px 15px 5px 15px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	transition: all 0.25s linear 0s;
	margin: 15px;
}

/*Groups*/
#web.signed_in #main .group:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
}

/*Groups*/
#web.signed_in #main .group a {
	color: #BBB;
}

/*Groups*/
#web.signed_in #main .group a:hover {
	color: #F30;
}

/*Groups*/
#web.signed_in #main .group h1 {
	color: #BBB;
}

/*< - - PEOPLE - - >*/
#friends_list.listing li.item,
.listing li.item {
	border: none;
}

.item.user {
	background: #161616;
	border-radius: 3px;
	padding: 7px 7px 7px 7px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333 !important;
	margin: 15px !important;
	text-transform: capitalize;
	transition: all 0.25s linear 0s;
}

.item.user:hover {
	border-color: #999 !important;
	transition: all 0.25s linear 0s;
}

.user_list .user .body .description h3 {
	margin-top: -22px !important;
}

.user_list .user .body .description h3 a {
	color: #BBB;
}

.user_list .user .body .description h3 a:hover {
	color: #F30;
}

.search_form div.text_input_fullwidth.search_input {
	border: 1px solid transparent;
	background: #2c2c2c;
	overflow: hidden;
	border-radius: 5px;
	width: 760px;
}

.search_form div.text_input_fullwidth.search_input.focus {
	color: #999 !important;
}


/*< - - INBOX PAGE - - >*/
#web.signed_in #main #notifications_list .notification.section {
	background: #161616;
	border-radius: 3px;
	padding: 7px 7px 7px 7px !important;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	border: 1px solid #333;
	margin: 15px;
}

#inbox_search.default_text {
	border: 1px solid transparent;
	background: #2c2c2c;
	overflow: hidden;
	border-radius: 5px;
	width: 730px;
}

#inbox_search.default_text.focus {
	color: #999 !important;
}

#unread_counter {
	border: 1px solid #FACDB0 !important;
	background-color: #F30 !important;
	-moz-animation-name: glow;
	-moz-animation-timing-function: ease-in;
	-moz-animation-iteration-count: infinite;
	-moz-animation-direction: alternate;
	-moz-animation-duration: 1000ms;
}

/*< - - SETTINGS - - >*/
#profile_settings .expandable .header a {
