	text-shadow: none !important;
	color: #AAA !important;
	transition: all 0.25s linear 0s;
}

#interests_list span.tag:hover {
	border-color: #999;
	transition: all 0.25s linear 0s;
	color: #FFF !important;
}

#privacy_settings form#update_privacy_form.edit_privacy_setting .section:not(:nth-child(2)) {
	background: #161616;
	border-radius: 3px;
	padding: 7px 7px 7px 7px !important;
