}

#privacy_settings form#update_privacy_form.edit_privacy_setting .section h2,
#privacy_settings form#update_privacy_form.edit_privacy_setting .section h3 {
	color: #CCC;
}

table tr td {
	border-bottom: none;
}

#update_notificaton_settings_form.edit_notification_setting table tbody tr {
	border-bottom: 1px solid #333;
}

#web.signed_in #notifications_list .notification.section.unread {
	border-color: #F30 !important;
	height: 16px;
}

#web.signed_in #notifications_list .notification.section.unread .body {
	position: relative;
	top: -6px;
}

#web.signed_in #notifications_list .notification.section.unread .body:before {
	content: "";
	background: url("http://i.imgur.com/TVrYoAF.png") -19px -22px;
	position: relative;
	top: 6px;
	left: 0px;
	width: 18px;
	height: 18px;
	display: inline-block;
	padding-right: 5px;
}

/*< - - INVITE YOUR FRIENDS - - >*/
#invite_friends form#invite_form.toggle_submit .section.borderless h1,
#web.signed_in #wrapper #inner #main #invite_friends form#invite_form label {
	color: #CCC;
}

#web.signed_in #invite_friends .text_input_fullwidth,
#web.signed_in #invite_friends #recipient_list.token-input-list {
	border: 1px solid #666;
