#post-review form textarea:not(.utility-button),
#post-comment form textarea:not(.utility-button) {
	background: #2c2c2c;
	border-color: transparent;
}

#post-review form input:not(.utility-button):hover,
#post-comment form input:not(.utility-button):hover,
#post-review form textarea:not(.utility-button):hover,
#post-comment form textarea:not(.utility-button):hover {
	border-color: #666;
}

input,
input.text,
input[type="text"] {
	color: #999;
}

table.index thead {
	background: url("http://i.imgur.com/C1v2n1W.png") repeat-x scroll 0 0 / 100% 100% !important;
	color: #999;
	border-radius: 3px;
	width: 670px;
}

.assistant_products table.index {
	background: #161616;
	border: 1px solid #333;
	border-radius: 3px;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
}

/*Possibly remove all borders*/
.assistant_products table.index td {
	border: none !important;
}

/*Removed borders, because; Borders, borders everywhere!*/
#recently-purchased .top-row,
#product-related-items,
#recently-purchased,
#product-reviews.clear div.tab-content.tab-content-border.sort-header,
#shopping-cart .cart-merchant .items-by-merchant,
#checkout .cart-merchant .items-by-merchant {
	border: none;
}

table.index td,
table.default td {
	border-color: #333;
}

#user-account-nav ul li a.active {
	background: none;
	border-bottom: 4px solid #5CB192;
	color: #FFF !important;
	border-radius: 0 !important;
}


tbody *,
#order-history,
#user-account,
#canvas [class*="items-by-merchant"] *,
.assistant_subscriptions table.index *,
.assistant_orders table.index {
	border-color: #333 !important;
}

#search-menu,
#category-menu ul,
#merchant-search-category,
#search-menu div.filter-options,
#merchant-search-category div.filter-options {
	border-color: #333;
}

.marketplace-sprite:not(.not-permitted):not(.inventory):not(h4),
.modal .close {
	background-image: url(https://farm5.staticflickr.com/4344/36779720146_8fae9f9a78_o.png) !important;
}

.reviews div.star-rating,
.reviews div.star-rating a {
	background-image: url(https://i.imgur.com/VZdV6fq.png) !important;
}

.advertisement {
	background: #161616;
	border-color: #333;
}

/*Background*/
.footer-paginate,
#product-merchant-details .cart-form a[href$="edit"]:not(.revise-listing) {
	background: none !important;
	border: none !important;
}

#merchant-top-nav > ul:nth-child(1) {
	position: absolute;
	width: 570px !important;
	float: none !important;
	top: -38px !important;
	left: 56%;
	margin-right: -56%;
	border-bottom: 1px solid #333;
}

#merchant-top-nav li.off,
#merchant-top-nav li.on {
	position: relative;
}

#merchant-top-nav li.off::before,
#merchant-top-nav li.on::before {
	content: '';
	position: absolute;
	bottom: 0;
	left: 50%;
	right: 50%;
	width: 0%;
	height: 3px;
	background: #508E77;
	transition: all .15s cubic-bezier(.79, .14, .15, .86);
}

#merchant-top-nav li.off:hover::before,
#merchant-top-nav li.on:hover::before {
	left: 0 !important;
	right: 0 !important;
	width: 100% !important;
}

#merchant-top-nav li.off:hover,
#merchant-top-nav li.on:hover {
	background: none !important;
}

#merchant-top-nav li ul {
	background: #161616;
	border: 1px solid #333;
	border-radius: 0px 0px 3px 3px;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
	margin-top: 0px !important;
}

#merchant-top-nav li ul a {
	color: #999 !important;
}

#merchant-top-nav li ul a:hover {
	color: #FFF !important;
	background: #444;
}

#inventory-import.closed h1 {
	background: #333 !important;
	border: 1px solid rgba(0, 0, 0, .8) !important;
	border-radius: 3px !important;
	box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
	text-shadow: none !important;
	color: #AAA !important;
	font-weight: bold !important;
}

#inventory-import.closed h1:hover {
	box-shadow: inset 0px 2px 4px 0px #000 !important;
	border-bottom-color: #333 !important;
	border-top-color: #000 !important;
	border-right-color: #222 !important;
	border-left-color: #202020 !important;
	background-color: #181818 !important;
	border-radius: 3px !important;
	transition: background .2s, box-shadow .4s, border-color .2s !important;
	color: #4C4C4C !important;
}

/*Removed contents*/
/*#marketplace-toolbar .span-2, */
#recently-purchased .span-2.last/*Advertisements.. we have enough of that.*/
,
#item-options .social-links.clear/*That Facebook "LIKE" button*/
,
#product-related-items .related-item .last {
	display: none;
}

.footer-paginate,
.sort-header {
	border-radius: 0 !important;
}

#ads,
#checkout {
	display: none !important;
}
}

@-moz-document domain("cashier.secondlife.com") {
html,
body {
	background: url('https://c2.staticflickr.com/9/8647/30219334841_014d10476f_o.jpg') 100% 100% !important;
	background-repeat: no-repeat !important;
	background-attachment: fixed !important;
	background-position: 25% !important;
	-webkit-background-size: cover !important;
	-o-background-size: cover !important;
	background-size: cover !important;
	overflow-y: auto !important;
	overflow-x: hidden !important;
}

#mainContent {
	background: #222 !important;
	/*← Just in case.*/
	background-clip: padding-box !important;
	border: 1px solid rgba(0, 0, 0, 0);
	box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8);
	padding: 20px;
	color: #999;
	border-radius: 5px;
}

hr,
#invoice .invoice-label,
#paymentContainer {
	border-color: #333 !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
	color: #5CB192;
}

[id*="Label"].invoice-row {
	border: 1px solid #333 !important;
	padding: 12px;
	padding-bottom: 3px !important;
	color: #56BD8B !important;
	font-weight: bold;
}
}

@-moz-document url-prefix("https://marketplace.secondlife.com/feeds/") {
html,
body,
#feedHandler {
	background: url('https://c2.staticflickr.com/9/8647/30219334841_014d10476f_o.jpg') 100% 100% !important;
	background-repeat: no-repeat !important;
	background-attachment: fixed !important;
	background-position: 25% !important;
	-webkit-background-size: cover !important;
	-o-background-size: cover !important;
	background-size: cover !important;
	overflow-y: auto !important;
	overflow-x: hidden !important;
}

body[onload*="SubscribeHandler"] {
	position: relative;
	margin: 0 auto;
	float: none;
	top: 50px;
}

#feedBody,
#feedHeaderContainer {
	background: #161616;
	border: 1px solid #333;
	box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
}
}
