style.css:
/******************
****** TABS *******
******************/
.tab-background-middle {
    margin: 0;
    border-style: none ;
    height: 28px;
    background: transparent !important;
}
.tabmail-tab:before {
    display: none !important;
}
.tabmail-tab {
    margin: 0 !important;
    margin-right: -1px !important;
}
.tabmail-arrowscrollbox > scrollbox {
    padding: 0 !important;
}
.tab-background-start,
.tab-background-end {
    display: none;
}
.tab-background {
    background: #333;
    margin: 0;
    background-image: none !important;
    border-top: solid 1px transparent;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.3);
}
.tabmail-tab[selected="true"] .tab-background {
    border-top-color: /*[[accent-color]]*/;
}

/******************
***** TOOLBAR *****
******************/
.mail-toolbox,
.chromeclass-menubar {
    background-image: none !important;
    background-color: #333 !important;
    border-top: none !important;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.3) !important;
}
.toolbarbutton-menubutton-dropmarker:before {
    display: none !important;
}

/******************
*** TODAY PANE ****
******************/
#today-pane-panel {
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.3);
}
splitter {
    border: none !important;
}
sidebarheader {
    background: none #555 !important;
}
#mini-day-box {
    background: #444;
    color: #CCC;
    border-top: none;
    box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.15);
    border: none;
    margin: 0;
    padding: 0;
}
#today-minimonth-box {
    background: #555;
    border: none;
    margin: 0;
    padding: 5px 0;
}
#today-Minimonth,
.minimonth-month-box {
    background: transparent;
    color: #CCC;
}
.minimonth-month-box {
    background: none !important;
    border: none !important;
}
.minimonth-row-head {
    font-weight: bold;
}
.minimonth-day {
   background: transparent !important;
   border: none !important;
}
.minimonth-day:not([othermonth="true"]) {
    color: /*[[accent-color]]*/;
}
.minimonth-day[selected="true"] {
    background-color: /*[[accent-color]]*/ !important;
    color: #333;
}
#agenda-toolbar {
    background: #555;
    border: none;
    padding: 5px;
}
#todaypane-new-event-button .toolbarbutton-icon {
    filter: invert(100%);
}
#todaypane-new-event-button {
    color: /*[[accent-color]]*/;
    padding: 5px;
    -moz-appearance: none;
    border: 0;
}
#agenda-listbox {
    border: none;
    background: #666;
    margin: 0;
    color: #CCC;
}
.agenda-checkbox[selected="true"] {
    background-color: /*[[accent-color]]*/;
    color: #333;
}

/******************
*** FOLDER PANE ***
******************/
#folderTree {
    background: #444 !important;
    box-shadow: inset 0 0 3px rgba(0, 0, 0, 0.5);
    color: #CCC;
}
treechildren::-moz-tree-cell(selected, focus) {
    background: /*[[accent-color]]*/;
    box-shadow: none;
    border-radius: 0;
}
treechildren[overflowing="true"] {
    color: #f00;
}
#folderpane_splitter {
    display: none;
}

/******************
*** THREAD PANE ***
******************/
#threadTree {
    background: #555;
    color: #CCC;
    box-shadow: inset -3px 0 3px rgba(0, 0, 0, 0.15);
}
treechildren {
    margin: 5px !important;
    border: solid 1px transparent !important;
    background: none !important;
}

/******************
** MESSAGE BODY ***
******************/
#messageBox,
#displayDeck {
    background: #666;
    border: none;
    box-shadow: none;
    margin: 0;
}
body {
    background: #666;
    overflow: auto;
}
body .bottom-links {
    color: #CCC;
}
a {
    color: #00F !important;
}
body .bottom-links a {
    color: /*[[accent-color]]*/ !important;
}
#conversationHeader {
    background: #333 !important;
    border: none;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.15);
    color: #FFF;
}
#conversationHeader .actions button {
    border: none;
    margin: 0;
    filter: invert(50%);
}
#conversationHeader .actions button:hover {
    filter: invert(100%);
}
body * {
    background: transparent;
}
.moz-text-html {
    background: #FFF;
    overflow: auto;
}

/******************
*** STATUS BAR ****
******************/
#status-bar {
    background: #333 !important;
}
#calendar-show-todaypane-panel {
    padding: 0;
}
#calendar-status-todaypane-button,
#calendar-status-todaypane-button:hover,
#calendar-status-todaypane-button[checked="true"] {
    border-radius: 0;
    border: none;
    box-shadow: none;
    margin: 0;
    padding: 0;
    background: none;
}
#calendar-status-todaypane-button:hover {
    color: /*[[accent-color]]*/;
}
/******************
*** ADDONS PAGE ***
******************/
#addons-page {
    background: linear-gradient(to bottom, #333 0%, #555 50%);
}
#addons-page #header toolbarbutton {
    background: none;
    border-radius: 0;
    border: none;
    box-shadow: none;
    filter: invert(100%);
}
#addons-page #categories {
    overflow: visible;
}
#addons-page .category{
    color: #FFF;
    border-radius: 0;
    margin-right: 1px;
}
#addons-page .category[selected="true"] {
    border: none;
    background: #888;
    box-shadow: -3px 0 3px rgba(0, 0, 0, 0.5);
}
#addons-page .addon[pending="uninstall"] {
    background: #CC7;
}
#addons-page .pending {
    color: #000;
}
#addons-page .pending button {
    background: #885;
    color: #FFF;
    padding: 5px;
    text-decoration: none;
    border-radius: 0;
}
#addons-page .main-content {
    border-radius: 0;
    border: none;
    background: #888;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
}
#addons-page .addon {
    border: none;
    padding: 2em 1em 2em 0;
    color: #333;
}
#addons-page .addon[selected="true"] {
    background: #999;
}
#addons-page .addon .name-container {
    color: #000;
}
#addons-page .addon:not([pending="uninstall"]) .button-link {
    color: /*[[accent-color]]*/;
}
#addons-page button.addon-control,
#addons-page .sorter {
    border-radius: 0;
    background: linear-gradient(to bottom, #444, #333);
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
    border: none;
    padding: 5px;
    color: #CCC;
}
#addons-page .addon-control.remove {
    background-color: #900;
}
slider{
    background: none;
}

