style.css:
/* Reattach Imgur Title Bar */
/* Author: Marcus Minhorst */

@namespace html url('http://www.w3.org/1999/xhtml');

@-moz-document domain('imgur.com') {
    .post-container {
        padding-top:
            0px !important;
    }
    
    .fixed-share-container {
        margin-top:
            40px !important;
    }
    
    .post-header {
        border-radius:
            5px 5px 0px 0px !important;
        position:
            relative !important;
    }
}
