style.css:
@-moz-document domain("idobata.io") {  
  a:hover, a:focus {
    color: #EEE;
  }
  a {
    color: #CCC;
  }
  .highlight-mention .mentioned, .sidebar.right, .sidebar.right section .header {
    background-color: #36312F;
  }
  nav.scroll-pane::-webkit-scrollbar-track, nav.scroll-pane::-webkit-scrollbar-track {
    border: none;
  }
  nav.scroll-pane::-webkit-scrollbar {
    display: none;
  }
  body, .toolbar.in-search, .form-control, .message-form button, .message-form label, .message-form textarea, main.vertical-container, .html code, .html pre, .html table tr, .html table tr:nth-child(2n), form {
    background-color: #36312F;
    color: #EFEFEF;
  }
  .message:not(:last-child) {
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }
  .text-muted {
    color: #999;
  }
  .sidebar.right nav > a {
    background-color: #524946;
    color: #EFEFEF;
    border-top: 1px solid rgba(0, 0, 0, 0.5);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }
  .html code, .html pre {
    border: 1px solid #524946;
  }
  .sidebar.right nav > a:hover, .sidebar.left nav > a.active:not(.ember-transitioning-out), .sidebar.left nav > a.ember-transitioning-in {
    background-color: #333;
    color: #FFE77F;
    border: 1px solid #111;
  }
  .sidebar.left nav > a.heading.active:not(.ember-transitioning-out), .sidebar.left nav > a.heading.ember-transitioning-in {
    background-color: #373230;
    color: #FFE77F;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }
  .panel-default > .panel-heading {
    background-color: #373230;
    color: #EFEFEF;
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }
  .list-group-item {
    background-color: #373230;
    color: #EFEFEF;
    border-top: 1px solid rgba(0, 0, 0, 0.5);
    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
  }
  .list-group-item:hover {
    background-color: #524946!important;
    color: #FFE77F!important;
  }
}
