style.css:
@-moz-document domain("phoenix.corvidae.org") {

 /* HEADER */ 

    #elLogo img {
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
     
        /* CHANGE THIS URL TO CHANGE THE HEADER IMAGE */
        background: url(http://i.imgur.com/ghpbKZI.png) no-repeat;      
        width: 429px;
        height: 54px;
        padding-left: 429px;
        padding-top: 15px;}


 /* LAYOUT PUSHDOWN */ 
.ipsNavBar_primary {margin-top: 40px;}


 /* LAYOUT COLORS */ 

#ipsLayout_header header {background-color: #151515;}

#elSearchNavContainer, #elMobileNav {background: #3F3F3F;}

.ipsWidget.ipsWidget_vertical .ipsWidget_title {background: #3F3F3F;}

.ipsType_sectionTitle {background: #232323;}

.ipsItemStatus.ipsItemStatus_large {background: #232323;}

.ipsButtonBar {background: #3F3F3F;}

a {color: #3F3F3F;}
a:hover {color: #696969;}

.ipsApp .ipsButton_important {background: #232323;}

#elHeaderSubLinks a, .ipsBreadcrumb > u{color: #232323;}

.ipsApp .ipsButton_alternate {background: #232323;}

.ipsCommentUnreadSeperator {border-color: #8D8D8D;}

.ipsRepBadge_positive, .ipsRepBadge_positive:hover {color: #232323 !important;}

.ipsType_normal {font-size: 12px; font-family: verdana;}

.ipsNavBar_primary{background-color: #363636;}

.ipsItemStatus:not(.ipsItemStatus_large){color:#151515;}

body{background-color:#F3F3F3;}

}
