style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#tabs-toolbar > * {

    background-color: #cececc !important;

    border-top: solid 1px transparent;

    border-top-color: #a1a1a1;

    height: 36px;
}

.tab-text.tab-label {

    font-weight: bold;

    color: #999 !important;

    padding-bottom: 2px !important;
}

.tab-background-middle {

    margin: 0 !important;

    border-style: none ;

    background: transparent !important;
}

.tabmail-tab:before {

    display: none !important;
}

.tabmail-tab {

    margin-right: 4px !important;

    margin-left: 4px !important;

    margin-bottom: 1px !important;

    text-align:center;

    height: 36px;

}

.tab-background-start,
.tab-background-end {

    display: none;
}

.tab-background {

    background: #cececc;

    margin: 0;

    background-image: none !important;
 }

.tabmail-tab[selected="true"] .tab-background {

   background-color: #dbdbd9;
  
   box-shadow: 0 -3px 0 #4a90d9 inset !important;

   border: 1px solid #bbbbb8;
}

.tabmail-tab[selected=true]:hover .tab-background {

   background-color: #ddd;
}

.tab-icon-image {

   margin-bottom: 2px !important;
}


.tabmail-tab[selected="true"] .tab-text {

   color: #000 !important;
}

.tabmail-tab:hover:not([selected]) .tab-background {

   background: #cececc !important;

   box-shadow: 0 -3px 0 #999 inset !important;

   border: 1px solid #bbbbb8;
}

.tabmail-tab:hover:not([selected]) .tab-text {

   color: #777 !important;
}

/* Close button on tabs... */

.tabmail-tab:not([selected]) .tab-close-button {

	visibility: visible !important;

        opacity: 0.5;
}

.tabmail-tab:not([selected]):hover .tab-close-button {

       visibility: visible !important;

       opacity: 0.7;
       
}


.tab-close-button:hover, .tab-close-button:active, .tab-close-button:focus,
.tab-close-button:hover[selected="true"], .tab-close-button:hover:active, 
.tab-close-button:hover:active[selected="true"]
 {

	opacity: 1;

        background-color: #e9e9e9;

        background-image: -moz-image-rect(url("chrome://global/skin/icons/close.svg"), 0, 16, 16, 0);

        box-shadow: 0 0 0 1px #eee, 0 0 0 2px #999;

        border-radius: 2px;
}

.tab-close-button {

        margin-bottom: 2px !important;
}
