style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigabase.com")  
{

/* 18 */
div.age18 ,    
/* footer */
div.container.text-center #footer #footerDiv {
   display: none !important;
}
/* блок линков Главная | Управление Подпиской на Контент | Пожаловаться на файл | Получить контент ... */   
div.container.text-center #footer  {
   padding-top: 0px !important;
   margin-top: 20px !important;
}     
/*  линки Главная | Управление Подпиской на Контент | Пожаловаться на файл | Получить контент ... */       
div.container.text-center #footer p a ,
/*  верхнее меню */   
div.navbar.navbar-default.navbar-static-top div a    
{
    color: #40989E ;  
}     
    
div.container.text-center #footer p a:hover ,
/*  верхнее меню */   
div.navbar.navbar-default.navbar-static-top div a:hover ,
/*  номера страниц  */
div.pager a:not([class="cur"]):hover  ,
/*  поиск. термы */
#UploadPage .c1 > H3 > a:hover ,    
/*  в рез-х поиска */
#UploadPage .c1 OL LI a:hover ,
/*  Популярные поисковые запросы:  */
div.text-center SPAN  a:hover ,
/*  Часто Задаваемые Вопросы */
div.container > div.text a[onclick^="showHide"]:hover    
{
    color: #CC0000 ;  
}  
    
}   /*   domain("gigabase.com") */



/* Скачать файл */
@-moz-document url-prefix("http://www.gigabase.com/getfile"),
               url-prefix("http://gigabase.com/getfile"),
               url-prefix("https://www.gigabase.com/getfile"),
               url-prefix("https://gigabase.com/getfile")
{

/*   Скачать файл: - размер   */
div.container H2 > SMALL:nth-child(3) {
    color: #FF6600 !important;  
} 

/*   кн. Скачать бесплатно и Premium доступ */
.gigaArea .inner #payPanel  .text-center ,
/*   Купить Premium */
.gigaArea .inner > H3 ,
/*   тарифы */    
.gigaArea .inner #upPayTariffs , 
/*   Pay  */    
.gigaArea .inner #pay3All ,
/*   Популярные поисковые запросы:  */  
.container div.text-center.hidden-xs.hidden-sm 
{
    display: none !important;
}
  
  
/* линк  нет, спасибо */
.gigaArea .inner #payPanel #noThanxDiv a.nothx1 {
    color: #fff ;  
    border: none !important;  
}  
.gigaArea .inner #payPanel #noThanxDiv a.nothx1::after  {
    content: "please click this link to continue";
    white-space: pre;
    color: #2072BD !important;  
    font-size: 30px !important;
    margin-left: -70px !important;
    position: relative   !important;
    top: 88px !important;
}
  
/* линк Скачать файл */  
.gigaArea .inner #dldLink {
    color: #2072BD ;
    font-size: 40px !important;
    position: relative   !important;
    top: 60px !important;
}
  
/* hover   */
.gigaArea .inner #payPanel #noThanxDiv a.nothx1:hover::after,
#dldLink:hover  {
    color: #22BD77 !important;  
}
  
  
}  /*   url-prefix("http://www.gigabase.com/getfile")   */
