style.css:
@-moz-document domain("www.sabrina-online.su") {
.quotetitle {
border-radius: 10px; /* скруглим углы заголовка цитаты */
padding: 0px 4px; /* слегка срежем пространство над и под текстом */
z-index: 2; /* заголовок должен лежать поверх белого поля цитаты */
position: relative; /* для чего позиционирование надо объявить относительным */
}
.quotecontent {
top: -6px; /* приподнимем белое поле на 6 пк */
z-index: 1; /* и заправим за заголовок */
position: relative; /* для чего позиционирование надо объявить относительным */
padding-top: 11px; /* спасём накрытый текст, расширим белое поле вверху на 6 пк */
margin-bottom: 4px; /* уменьшим пустоту под цитатой на 6 пк, она и без того велика */
  }
.quotetitle, .quotecontent {
	border: #ccc dotted 2px;
}

/* paginator */
#pagecontent > table:not(.tablebg) td:last-of-type strong::before {
	content: '[';
}
#pagecontent > table:not(.tablebg) td:last-of-type strong {
	font-size: 110%;
}
#pagecontent > table:not(.tablebg) td:last-of-type strong::after {
	content: ']';
}
}
