style.css:
@-moz-document regexp(".*/d2l/.*") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/oMMgfZMQthOryQo9n22dcuvvDin1pK8aKteLpeZ5c0A.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
.d2l-font {
    font-family: 'Roboto';
    font-size: 12px;
}

.d2l-menuflyout-light .d2l-menuflyout-text, .d2l-menuflyout-light .d2l-menuflyout-link-link, .d2l-menuflyout-light .d2l-menuflyout-link-link:link, .d2l-menuflyout-light .d2l-menuflyout-link-link:hover, .d2l-menuflyout-light .d2l-menuflyout-link-link:visited {
	color: #fff;
}

.d2l-background-left, .d2l-background-right, .d2l-twopanelselector-wrapper {
    background-image: none ;
    background-color: transparent;
}

#d2l_navbar .d2l-navbar-title {
    color: #FFFFFF;
    font-family: sans-serif;
    text-shadow: 1px 2px #202020;
    padding: 2px 2px 2px 30px;
}

.d2l-page-main {
    background-color: transparent !important;
}

.d2l-heading {
    color: #000;
}

.d2l-htmlblock {
    color: #202020;
}

.d2l-widget-header {
    background-image: none ;
    background-color: /*[[headbg]]*/;
    color: #FFFFFF;
    border: none;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}

.d2l-page-main-padding {
    border: none;
    background-color: transparent;
    background-image: none;
}

.d2l-minibar {
    background-color: /*[[headbg]]*/;
    color: #FFFFFF ;
    border: none;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    font-size: 14px;
}

.d2l-widget-content {
    color: #202020;
    border: none;
}

.d2l-navbar-link span, #d2l_navbar .d2l-menuflyout span {
    background-color: transparent ;
    border: none ;
    color: /*[[linkclr]]*/;
}

.d2l-navbar-link span:hover, .d2l-navbar-item a:hover, .d2l-menuflyout span:hover, .d2l-menuflyout a:hover {
    text-decoration: underline;
}

#d2l_navbar .d2l-navbar-sep {
    background-image: none ;
    background-color: transparent ;
    border: none ;
}

.d2l-navbar-link, .d2l-navbar-item a, .d2l-menuflyout a, .d2l-navbar-area-list * {
    background-color: transparent;
    border: none !important;
    font-style: normal !important;
    text-transform: none !important;
    color: /*[[linkclr]]*/ !important;
    font-size: 14px !important;
    font-weight: 400 !important;
}

.d2l-page-main .d2l-link, .d2l-htmlblock a {
    color: /*[[linkclr]]*/ !important;
}

.d2l-homepage-header-wrapper .d2l-heading.vui-heading-2 {
    color: #fff;
}

div.d2l-page-message.d2l_1_51_43 {
    color: #000;
}

.d2l-dialog {
    background-color: transparent;
    border: none;
    border-radius: 0;
    -webkit-border-radius: 0;
}

.d2l-dialog-title {
    background-color: /*[[headbg]]*/;
    border: none;
    border-radius: 0;
    -webkit-border-radius: 0;
    box-shadow: none;
    -webkit-box-shadow: none;
}

.d2l-dialogue-close {
    border: none;
}

.d2l-dialogue-width > * {
    background-color: #fff;
    border: none;
    border-radius: 0;
    -webkit-border-radius: 0;
    box-shadow: none;
    -webkit-box-shadow: none;
}

.d2l-shim-opaque {
    background-color: #000;
}

ul.d2l-datalist {
    margin: 0.5rem 0 0.5rem 0;
    border: 1px solid #e0e0e0;
    border-radius: 2px;
    overflow: hidden;
}

.d2l-menuflyout-contents ul.d2l-datalist {
    border: none;
}

.d2l-datalist-item-content {
    padding: 10px;
}

li.d2l-datalist-item-content > .d2l_2_14_835 > a.d2l-link, li.d2l-datalist-item-content > .d2l_2_14_835 > div.d2l-textblock {
    background-color: #fff;
    line-height: 1.5rem;
    margin: 0;
    display: block;
    float: none;
    color: #26a69a;
}

.d2l-menuflyout-contents {
    border: none !important;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    border-radius: 0;
    z-index: 100;
    background: #fff;
	transition: height 0.25s;
} .d2l-menuflyout-contents[aria-expanded=false] {
    height: 0px;
} .d2l-menuflyout-contents[aria-expanded=true] {
    height: auto;
}

.vui-list>.vui-selected, table.d_gl tr th.d_hch, table.d_gl tr.d_ggl1, table.d_gn tr.d_ggl1 {
    background-color: /*[[hi-lt]]*/ !important;
}

.vui-list>.vui-active.vui-selected {
    background-color: /*[[hi-dk]]*/ !important;
}

table.d_g, table.d_gd tr.d_gd, table.d_gl tr.d_gd {
    background-color: transparent;
}

.d2l-navbar-group-inner {
    padding: 0;
}

.d2l-navbar-group-inner li {
    margin: 6px 10px;
}

div.d2l-page-bg div, table {
    background-color: #fff;
}

.d2l-menuflyout-group-container {
    border: none;
}

.d2l_1_139_419 {
    background-color: /*[[headbg]]*/;
}

/* Collections / trees */
.d2l-datalist-nesteditem-accordion.li:first-child, .d2l-le-TreeAccordion {
	border: 1px solid #e0e0e0;
  border-radius: 2px;
  overflow: hidden;
  position: relative;
}

.d2l-le-TreeAccordion .d2l-le-TreeAccordionItem-wrapper {
	background-image: none;
	background-color: transparent;
    line-height: 1.5rem;
    padding: 0 6px;
    margin: 0;
	border: none;
    border-bottom: 1px solid #e0e0e0;
}

.d2l-le-TreeAccordionItem-SelectedRoot, .d2l-le-TreeAccordionItem-Selected {
	background-color: #e7e7e7;
}
.d2l-le-TreeAccordionItem-ActiveRoot, .d2l-le-TreeAccordionItem-Active {
	background-color: #f7f7f7;
}


.d2l-progresschart rect {
	fill: /*[[headbg]]*/;
	stroke: /*[[headbg]]*/;
}
}
