style.css:
@-moz-document domain("theweek.com") {

div#sandwich-search {
    position: fixed;
    top: 2mm;
    left: 2mm;
    padding: 0;
    margin: 0;
}

div#header {
    width: 0;
    overflow: hidden;
}


/* pruning nuisances... */

[class$="-ad"], [class*="-ad-"] {
    display: none !important;
}

div#sticky-social_583630 {
    display: none !important;
}

.more-from-the-author #module-recirculation > .zerg-colm, /* off-site articles */
div#fb-root, /* share */
.fb-content.top-section,
div#art-subscribe-unit-outer /* subscribe */ {
    display: none;
}

}

@-moz-document domain("theweek.com") {

.desktop-ad, .managed-ad, .article_second_intext_ad, .onScrollAds {
  display: none !important;
}

}
