style.css:
/*
Idea attribution: https://news.ycombinator.com/item?id=10437347
*/
@-moz-document domain("fontsinuse.com") {
	.font-samples:hover li:hover,
	.list-thumb:hover .font-samples {
		transition: 0.5s opacity linear;
	}
	.font-samples:hover li:not(:hover) ,
	.list-thumb:not(:hover) .font-samples {
		opacity: 0;
	}
}
