style.css:
@-moz-document domain("furaffinity.net") {
#footer {
    min-height:140px;
}
.flow img {
    border-radius:4px;
}
.r-general a, .r-mature a, .r-adult a {
    border-radius:4px;
}
.profilepic {
    border-radius:8px;
}
.row1 h2 {
    color:#fda936;
}
.sitebanner {
    background:#000000;
    background: url('https://www.furaffinity.net/themes/beta/img/banners/logo/header_new.png');
    background: -webkit-linear-gradient(rgba(0,0,0,0) 60%, rgba(0,0,0,0.6)), url('https://www.furaffinity.net/themes/beta/img/banners/logo/header_new.png');
    background: -o-linear-gradient(rgba(0,0,0,0) 60%, rgba(0,0,0,0.6)), url('https://www.furaffinity.net/themes/beta/img/banners/logo/header_new.png');
    background: -moz-linear-gradient(rgba(0,0,0,0) 60%, rgba(0,0,0,0.6)), url('https://www.furaffinity.net/themes/beta/img/banners/logo/header_new.png');
    background: linear-gradient(rgba(0,0,0,0) 60%, rgba(0,0,0,0.6)), url('https://www.furaffinity.net/themes/beta/img/banners/logo/header_new.png');
}
.shrinkyicon {
    border-radius:4px;
}
.current h2 {
    color:#fda936;
}
.rounded, #shoutboxentry .leftcol, #searchbox input[type=search], textarea, .textarea, .textbox, select, .uploadfield, .avataruploadfield, .tags a, .usercommentbubble, .responsenav, .pmviewbubble, .usershoutbubble, center.flow.messagecenter b {
    border-radius:4px;
}
.roundedtop {
    border-top-right-radius:4px;
    border-top-left-radius:4px;
}
.roundedbottom {
    border-bottom-right-radius:4px;
    border-bottom-left-radius:4px;
}
}
