style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
/***** GENERAL *****/
/* header */
#page, #yt-masthead-container{
    background: #333333;
}
#yt-masthead-container{
    border-bottom: 1px solid black;
}

/* logo */
.logo.yt-sprite:not([usemap]) {background-image: url("http://i.imgur.com/1JUpIsx.png") !important;}
.logo.yt-sprite:not([usemap])
{
    width: 0px !important;
    padding-left: 120px !important;
    height: 38px !important;
    background-position: 0px 0px !important;
    background-size: auto auto !important;
    margin-right: -28px !important;
}

/* menu */
#appbar-guide-menu{
    margin-top: 56px;
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu{
    background: #333;
    border-right: 1px solid #333;
}
#guide-container{
    background: #444444;
}
.guide-toplevel a, .guide-toplevel span, a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item, .branded-page-module-title-text{
    color: #FFF;
}
.guide-flyout, .appbar-content-hidable{
    background: #555555;
    border: 0;
}
.guide-item-subtitle{
    color: #CCC;
    text-shadow: none;
}
.yt-uix-button-empty .yt-uix-button-icon-wrapper span, .yt-uix-button-empty.yt-uix-button-has-icon span{
	border-radius: 1px;
}

/* SCROLL */
.yt-scrollbar ::-webkit-scrollbar-track-piece, .yt-scrollbar ::-webkit-scrollbar-track{
	background: #555;
}
.yt-scrollbar ::-webkit-scrollbar-thumb{
	background: #999;
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu{
    border-right: 1px solid #333;
}

/***** INICIO *****/
/* content */
#appbar-nav{
	background: #444;
}
#feed-main-what_to_watch, #feed-main-what_to_watch a{
	background: #555;
	color: #FFF;
}
#feed-main-what_to_watch .feed-item-dismissable .branded-page-module-title-text, .search-header *{
	color: #FFF;
}
/* FIX RESULT COLOR */
.search-header .num-results, .search-header .num-results strong, .search-header .yt-uix-button-content{
	color: #FFF;
}
/**/
#feed-main-what_to_watch .feed-item-dismissable span{
	color: #AAA;
}
#feed-main-what_to_watch .yt-lockup-byline, #feed-main-what_to_watch .yt-lockup-meta{
	color: #AAA;
}
#feed-main-what_to_watch .yt-lockup-byline a{
	color: #DDD;
}
#watch7-sidebar .video-list-item a:visited .title, #watch7-sidebar .video-list-item a:visited .title .yt-deemphasized-text, .yt-uix-button .yt-uix-button-content .unsubscribe-label, .yt-uix-button .yt-uix-button-content .subscribe-label, .yt-uix-button .yt-uix-button-content .subscribed-label{
	color: #FFF;
}
.yt-lockup-content .yt-lockup-description, 
.branded-page-v2-body, .yt-card, .yt-ui-ellipsis{
	background: #555;
	color: #FFF;
}
.branded-page-v2-body .load-more-text{
    color: #666;
}
/* HISTORIAL */
#browse-items-primary .yt-uix-button-empty .yt-uix-button-icon-wrapper span, #browse-items-primary .yt-uix-button-empty.yt-uix-button-has-icon span{
    background-color: #555;
    border-radius: 10px;
}
.yt-uix-button-icon-dismissal{
    background: no-repeat url(https://s.ytimg.com/yts/imgbin/www-hitchhiker-vfl8MN0m-.webp) -88px -1105px #555;
    background-size: auto;
    width: 20px;
    height: 20px;
}
.yt-uix-button-empty .yt-uix-button-icon-wrapper, .yt-uix-button-empty.yt-uix-button-has-icon.no-icon-markup:before{
    margin-right: 6px;
}


/***** REPRODUCCION *****/
/* blocks */
/*#placeholder-player, .player-api{
    margin-top: -100px;
}*/
#watch-header, #watch-action-panels, #action-panel-details, #watch-discussion, #watch7-sidebar-contents{
	background: #555;
	color: #FFF;
	text-shadow: 0px 0px 2px rgba(255, 255, 255, .1);
}
#watch-header span, #watch-action-panels span, #watch-description, .watch-sidebar-section label, .watch-sidebar-head, .watch-sidebar-body ul li a span, .video-list .video-list-item .description, #watch7-sidebar-modules .autoplay-bar .watch-sidebar-head, .video-list .video-list-item .title, #watch-header .yt-user-info a, .channel-header .branded-page-header-title .branded-page-header-title-link, .epic-nav-item-heading{
	color: #FFF;
}
	/* subtextos */
    .comments .comment-footer-action, .comments .comment-header .time a, .video-list .video-list-item .stat, .channel-navigation-menu .yt-uix-button .yt-uix-button-content, .watch-view-count{
		color: #CCC;
    }
	/* Nav Menu */
	.appbar-nav-menu .yt-uix-button .yt-uix-button-content{
		color: #FFF;
	}
	/* botones */
    .yt-uix-button-subscription-container .yt-uix-button, .yt-uix-button-subscription-container .yt-subscriber-count{
		background: #333;
		border: 1px solid black
    }

	/* metas */
	#action-panel-details a, .lohp-video-metadata .view-count{
    	color: #ccc;                                        
	}

/* Comentarios */
#yt-comments-sb-standin .callout-inner, #yt-comments-sb-standin .callout-outer, #yt-comments-sb-standin .box{
    background: #333;
}

#yt-comments-sb-standin .callout{
    display: none;
}

.all-comments, .all-comments a{
    background-color: #555;
    color: #CCC;
}

/***** UPLOAD ****/
.upload-item{
    background-color: #FFF;
}

/***** OTROS *****/
/* Otros botones */
#yt-masthead-user .yt-uix-button-content, #footer .yt-uix-button > .yt-uix-button-content , .watch-view-count{
    color: #333; 
}
.yt-uix-button-content .subscribe-label{
    color: #FFF;
}
.mod-button{
    padding: 3px 0 0 15px;
    -webkit-box-shadow: inset 0px 0px 65px 0px rgba(255, 255, 255, 0.55);
	-moz-box-shadow:    inset 0px 0px 65px 0px rgba(255, 255, 255, 0.55);
	box-shadow:         inset 0px 0px 65px 0px rgba(255, 255, 255, 0.55);
    border-radius: 3px;
    border: 1px solid #555;
}

.actions .mod-buttonbar .mod-button{
    border-radius: 6px;
}

/* text-shadow: 0px 0px 2px rgba(255, 255, 255, .1); */

/* END SECTION */
}
