style.css:
@-moz-document url-prefix("http://love.163.com/messages") {
#header {
    display: none;
}
.content {
    padding-top: 0;
    width: auto;
}
.pNotify .conL {
    width: 100%;
    padding: 10px;
    box-sizing: border-box;
}
#toolBar,
#scrollBar,
.titleBar h2,
.label-tag,
.warning-tips,
.visitor-tips,
.footer,
.mess-tips-close,
.notice-visitor-buyVip,
.conR {
    display: none;
}
.avatar {
    margin: 0 auto;
    width: 40px;
    height: 40px;
}
.notice-main {
    margin-left: 80px;
}
.avatar img {
    width: 100%;
    height: 100%;
}
.visitor-bd,
.notice-bd {
    min-height: 20px;
}
.notice {
    padding: 10px 0;
}
.notice-visitor,
.notice-digg,
.notice-heed {
    padding: 0;
    border: none;
    background-color: #fff !important;
}
.notice-visitor .avatar,
.notice-digg .avatar,
.notice-heed .avatar {
    width: 25px;
    height: 25px;
}
.notice .notice-info {
    margin: 5px !important;
}
.poplayer-sendMessage .poplayer-main,
.poplayer-sendMessage .publish-textarea textarea {
    width: 100%;
}
}
