style.css:
@-moz-document domain("gifsound.com") {
body {
    background-color: #000;
}
div[style] {
   background: #232323 !important;
}
a[style] {
    color: #7D7D7D !important;
}
a:hover[style]{
    color: #999 !important;
}
strong, b {
    font-weight: bold;
    color: #FFF;
}
form {
    background-color: #232323;
}
.score.unvoted{
    color: #FFF;
}
a:-webkit-any-link{
	color: #7D7D7D;
}
a:-webkit-any-link:hover{
    color: #999;
}
td, th {
    font-weight: bold;
    color: #FFF;
}
}
