style.css:
@-moz-document regexp("https?://web.de/.*") {
/*
 * Name:    web.de Login & Postfach + optionalen Dark Mode
 * Author:  NORWIN  "https://userstyles.org/users/110629"
*/

/************************login/out****************************/
/*[[darkl]]*/


section:not([data-content-login=""]):not(.hint-wrapper):not([class="logout-notification"]), #mainnav, #header-image-link, #weather, #promoline, #footer, .ad-component, #loginsearch-login .tab-nav, .breaking-news, #loginsearch-search, [data-device-type="desktop"] .info-layer, .ad-background, iframe, #header .search-wrapper, .statistics, .logout-notification .hint p, .notification-alert, .weather, .cookie-lasche-hp, .wrapper-center:not(.first) {display:none!important;}
#header, .wrapper-themed.webcent .wrapper-center, .wrapper-themed.webcent, .wrapper-center, .main-content {background: transparent!important;border:none!important;}

.adWithOutsideComponent .wrapper-center, .adWithOutsideComponent .wrapper-border{background:transparent!important;top:0px!important;border:none!important;}
  
body{padding-right: 0px!important;padding-left: 0px!important;}
body::before, .adWithOutsideComponent .wrapper-center.first, .adWithOutsideComponent .wrapper-center.first .wrapper-border::before {border:none!important;}
#container {margin: auto!important;}

section#loginsearch {display: inline!important;}
section.hint-wrapper {display: block!important;}
section.logout-notification{display: block!important;}
#loginsearch-login .tab-contentPart{display:inline!important}
#loginsearch-login .tab-contentPart:not(:nth-child(2)) {display:none!important}

#loginsearch {background: #D2E1EA!important;margin-top: 100px!important;background-image: none!important;height: 100px!important;padding:10px!important}
#loginsearch-login{background-color: transparent!important;}

/*stonecrusherimport*/
.ie7fix ~ *,#loginsearch-ad { display:none !important; }
.hint h2 + p { display:none !important; }
[data-hint="timeout"].hint-error > p { display:block !important; }
/*stonecrusherimport*/

/*fail login*/
body [data-hint="login-error"] p {display:block!important;}
.hint-error, #loginsearch .hint-wrapper .r4{background:transparent!important;border:none!important;}
.hint-error:after,#loginsearch .hint-wrapper::after{display:none!important;}
.r4 .hint p{line-height: 15px!important;}
}

@-moz-document regexp("https?://(navigator|home.navigator|home-bap.navigator|bap.navigator|addressbook.navigator|addressbook-bap.navigator|3c|3c-bap|kalender|ums-fax|ums-new|uas).web.de/.*") {
/************************navi****************************/
/****hauptseite****/
/*[[darke]]*/


/*html, body{height: calc(100% - 5px)!important;}*/

.grid-container.grid-left .grid-container:nth-child(2), ul.lr, .werbefrei, #billboard, .specialad {display:none!important;}
#desk div.grid-container.grid-right, #desk iframe {display:none!important;}

/*kaufen button*/
[title="Weiter"], #main .goto-mailbox{left: 783px!important;border: none!important;margin: 0!important;padding: 0!important;}
[title="Kaufen"], #main .btn-right-top span:nth-child(2){display:none!important;}
.page-splash .module-agb .moduleContainer .moduleContent div span{border:none!important;}

#desk {margin-top: 10px!important;}
  
[data-area="kiosk"], #kiosk iframe, [data-area="produkte"], [title="WEB.DE Club Center"], [title="Musik-Flatrate"], [title="Mobilfunk"], [title="Eigene Homepage"], [title="PC-Sicherheit"], [title="MillionenKlick"],[title="Millionenklick"], [title="Lotto"], [title="Games"], .iba, [title="Virenschutz"],[title="WEB.Cent"], [data-test="atl-actions-menu__item-more"], [data-item-name="allnet"], [data-item-name="maildomain_new"], [data-item-name="premiumclub_ha"], [data-item-name="zattoo"], [icon="core_search"], [data-item-name="deezer"], [data-item-name="tax"]{display:none!important}

/****adressbuch****/
#content-sky {display:none!important;}
#regions {width: 100%!important;max-width: calc(100% - 16px)!important}
#regions #list,#regions #edit {width: 50%!important;max-width: 50%!important;}

/****postfach****/
.ad.sky {display:none!important;}
.section-content{margin-right: 0px!important;}
  
[class="new iba"], [class="iba"], [class="iba selected marked marked-first marked-last"], .iba, .iba-uncertain, .iba-acceptable, .iba-vague, .iba-arm{display:none!important;}

/*stonecrusherimport*/
.page{min-width: 50.7rem !important;}
.section-container { bottom: 0 !important; }
#superbanner { display: none !important; } 
/*stonecrusherimport*/
}

@-moz-document url-prefix("https://info.web.de") {
/****hauptseite info text****/
a, span, div {display: none!important;}
}

options.value:
/*new login*/
section:not([data-content-login=""]):not(.hint-wrapper):not([class="logout-notification"]), [data-component="block-navigation"],
[data-col-count="4"] [data-hidden-in="1col"],
[data-col-count="4"] header[data-component="header"] > [data-wrapper] [data-component="searchbar"],
[data-component="login"] > p[aria-label],
[data-component="login"] > [data-component="bento"],
[data-col-count="4"] [data-component="login"] > div:first-child,
[data-login-sticky-indicator=""],
[data-container="right"],
[data-container="left"]
{display:none!important;}

.main {
justify-content: var(--justify-grid-content)!important;
grid-template-columns: calc(var(--col-count)*var(--teaser-width) + (var(--col-count) - 1)*var(--grid-column-gap) + 2*var(--grid-border))!important;}

header[data-component="header"]
{background-color: transparent!important;border:none!important;}

[data-content] [data-content-login] 
{background-color: transparent!important; padding: 24px var(--grid-border) 16px;border:none!important;justify-content: center!important;-webkit-box-pack: center!important;}

[data-content],
header[data-component="header"] > [data-wrapper]
{background-color: transparent!important;border:none!important;}

[data-component="login"]
{background: #D2E1EA !important;padding:50px 500px 70px 20px!important;border-radius: 15px!important;}

[data-component="input"] input, [data-component="button"][data-importance="input"]{background: #fff!important;border-color:#fff!important;}

