html, body, html:not(.adWithOutsideComponent) .wrapper-themed.games, .wrapper-themed.games .wrapper-center, html:not(.adWithOutsideComponent) .wrapper-themed.vorteilswelt, .wrapper-themed.vorteilswelt .wrapper-center, .wrapper-center.first:before, .wrapper-themed.special .wrapper-center, .wrapper-themed.special {background:#444!important}
html #loginsearch, .logout-notification .hint{background:#212121 url(https://file1.hpage.com/010270/19/bilder/commentsback1.jpg)!important;border:0px solid #4c4c4c!important;border-radius: 15px!important; box-shadow:none!important;color:#bbb!important;opacity:0.8;}
#loginsearch a, .logout-notification .hint h2, .logout-notification .link-list li > a{color: #bbb!important;}
#loginsearch p{color: #fff!important;}
.form-l input[type="text"], .form-l input[type="email"], .form-l input[type="password"], [value="Login"],[data-hint="login-error"] .btn-wrapper > *, .btn-service.btn-fix.btn-m{background: #444!important;border:none!important;box-shadow:none!important;color:#bbb!important}
.btn-m > input {height: 32px!important;}
#logo{filter:invert(1) grayscale(100%) opacity(0.6)}
body {border-top: 4px solid #353535!important;}
#helplinks, #helplinks a{background:#353535!important; color:#bbb!important}
#helplinks img{filter:invert(1) grayscale(100%) opacity(0.6)}
.logout-notification .link-list .btn-wrapper > *{background: transparent!important;border:none!important;box-shadow:none!important;color:#bbb!important}
[data-hint="login-error"] h2{color:#bbb!important}
/***enddark***/

/*new login*/
section:not([data-content-login=""]):not(.hint-wrapper):not([class="logout-notification"]), [data-component="block-navigation"],
[data-col-count="4"] [data-hidden-in="1col"],
[data-col-count="4"] header[data-component="header"] > [data-wrapper] [data-component="searchbar"],
[data-component="login"] > p[aria-label],
[data-component="login"] > [data-component="bento"],
[data-col-count="4"] [data-component="login"] > div:first-child,
[data-login-sticky-indicator=""],
[data-container="right"],
[data-container="left"]
{display:none!important;}

.main {
justify-content: var(--justify-grid-content)!important;
grid-template-columns: calc(var(--col-count)*var(--teaser-width) + (var(--col-count) - 1)*var(--grid-column-gap) + 2*var(--grid-border))!important;}

header[data-component="header"]
{background-color: transparent!important;border:none!important;}

[data-part="logo"]
{filter:invert(1) grayscale(100%) opacity(0.6)}

