[data-content],
header[data-component="header"] > [data-wrapper]
{background-color: transparent!important;border:none!important;}

[data-component="login"]
{background:#212121 url(https://file1.hpage.com/010270/19/bilder/commentsback1.jpg)!important;padding:50px 500px 70px 20px!important;border-radius: 15px!important;}

[data-component="login"] *{color:#bbb!important;}

[data-component="button"][data-importance="primary"]:hover {background: #333;}
[data-component="button"][data-importance="primary"] {background: #444}

[data-component="input"] input, [data-component="button"][data-importance="input"]{background: #444!important;border-color:#444!important;} 
/**end new login**/

/***dark***/
.atl-app-contents-wrapper, .notouch, html .app, html.circles, html.darkblue, html.darkpurple, html.darkgrey, html.beach, html.cristal, html.wall, html.print, html.beige, html.turquoise, html.lila, html.fmclassic, html.blue  {background: #333!important;}
/*navtop*/
.atl-navbar{background:#212121 url(https://file1.hpage.com/010270/19/bilder/commentsback1.jpg)!important;border-bottom:1px solid #4c4c4c!important; height: 52px!important;border-top:none!important;}
.pos-brand-logo .pos-svg-icon .pos-svg {width: 50px!important;filter: invert(100%) grayscale(100%) opacity(50%);}
.pos-brand-logo .pos-svg-icon {width: 50px!important;height: 52px!important;margin: 0 15px 0 6px!important;}
.atl-actions-menu__item:hover, .atl-actions-menu__item--active, .atl-actions-menu__item--active:hover{background: #333!important;}
html .atl-actions-menu__item--active .pos-icon-item__label{color: #212121!important;}
.atl-actions-menu__visible .pos-icon-item__label, .pos-brand-title{color: #bbb!important;}
.atl-navbar .pos-icon-item__icon .pos-svg{fill: #bbb!important;}
.atl-navbar .atl-actions-menu__item--active .pos-svg{fill: #212121!important;}
/*navleft*/
#navigation li a{color: #bbb!important;}
#navigation li.headline {color: #bbb!important;border-color: #4c4c4c!important;}
#navigation li .arrow{background: #4c4c4c!important;border-color: #4c4c4c!important;}
#navigation li a:before, #navigation li .arrow:before{filter: grayscale(100%) brightness(150%);}
#navigation:not(.nohover) li.item:hover{background: #212121!important;}
html.resolution-1024 #navigation ul{background: #212121!important;box-shadow:none!important;}
html.resolution-1024 #navigation ul li {background-color: transparent!important;}
#navigation button.navigation-expand, #navigation button.navigation-collapse{filter: grayscale(100%) brightness(30%);}
/*footer*/
.page_footer {background:#212121 url(https://file1.hpage.com/010270/19/bilder/commentsback1.jpg)!important;border-top:1px solid #4c4c4c!important;}
.page_footer a{color: #bbb!important;}
[pos-button].pos-button.atl-feedback__button, .pos-button--primary{background: #444!important;border:none!important;box-shadow:none!important;}
/*mailbox*/
.component.widget.mail > section{background:#212121 url(https://file1.hpage.com/010270/19/bilder/commentsback1.jpg)!important;border:1px solid #4c4c4c!important;border-radius: 15px!important; box-shadow:none!important;color:#bbb!important;opacity:0.8;}
.component.widget.mail a, .component.widget.mail .top .unread-box .number {color: #fff!important;}
.component.widget.mail ul.inbox-container {color: #888!important;}
.component.widget.mail .top .unread-box .number.bg, .component.widget.mail ul.nav li.active, .component.widget.mail ul.inbox-container li .options span{background:#666!important;}
.component.widget.mail ul.nav li.active:before{border-top: 7px solid #666!important;}
.component.widget.mail ul.nav li, .component.widget.mail ul.inbox-container li:hover{background:#212121!important;color: #bbb!important;}
.component.widget.mail .nav-tabs{border:none!important}
.component.widget.mail .tab-mail:after, .component.widget.mail .tab-mail:before{box-shadow: 0 0 10px 6px rgba(206, 218, 226, 0.25)!important;}
[src="//img.ui-portal.de/apps/touchdown-templates/smileys//smiley_0.gif"]{height:0px!important;background: url(https://file1.hpage.com/010270/19/bilder/smiley_0.png)no-repeat;padding: 35px;opacity:0.9;}
[src="//img.ui-portal.de/apps/touchdown-templates/smileys//smiley_0a.gif"]{height:0px!important;background: url(https://file1.hpage.com/010270/19/bilder/smiley_0a.png)no-repeat;padding: 35px;opacity:0.9;}
.component.widget.mail > section::after, .component.widget.mail > section::before{background: transparent!important;border:none!important;box-shadow:none!important;}
.component.widget.mail .tab-storage .row .col, .component.widget.mail .tab-webcent .webcents{width:auto!important}
/*mailseite*/
