.section-container .cke_toolgroup, .section-container a.cke_combo_button{background: #444!important;box-shadow:none!important;border:#444 1px solid!important;}
a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active,.ftd-box .button *{background: #666!important;}
